import React from "react";
import { useNavigate } from "react-router-dom";

// Images - replace with your actual images in src/assets/
import logo from "../assets/logo.png";
import bridalImg from "../assets/bridal.jpg";
import everydayImg from "../assets/everyday.jpg";
import luxuryImg from "../assets/luxury.jpg";

const Home = () => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen flex flex-col">
      {/* Header with Logo */}
      <header className="flex items-center justify-center py-6 bg-white shadow-md">
        <img src={logo} alt="Elegant Jewelry Logo" className="h-16 mr-4" />
        <h1 className="text-4xl font-extrabold font-serif text-pink-700">
          Elegant Jewelry
        </h1>
      </header>

      {/* Welcome Section */}
      <section className="text-center py-12 bg-gradient-to-r from-pink-100 to-yellow-100">
        <h2 className="text-5xl font-bold mb-4 text-pink-600">Welcome to Elegant Jewelry</h2>
        <p className="text-gray-700 text-lg mb-8">
          Explore our stunning collections for every occasion
        </p>
      </section>

      {/* Collections Section */}
      <section className="flex flex-col md:flex-row justify-center items-center gap-6 my-12 px-4 md:px-20">
        {/* Bridal */}
        <div className="relative group cursor-pointer">
          <img
            src={bridalImg}
            alt="Bridal Collection"
            className="rounded-xl shadow-lg transform group-hover:scale-105 transition"
          />
          <h3 className="absolute bottom-4 left-1/2 transform -translate-x-1/2 text-white font-bold bg-pink-600 px-4 py-2 rounded">
            Bridal Collection
          </h3>
        </div>

        {/* Everyday */}
        <div className="relative group cursor-pointer">
          <img
            src={everydayImg}
            alt="Everyday Wear"
            className="rounded-xl shadow-lg transform group-hover:scale-105 transition"
          />
          <h3 className="absolute bottom-4 left-1/2 transform -translate-x-1/2 text-white font-bold bg-yellow-500 px-4 py-2 rounded">
            Everyday Wear
          </h3>
        </div>

        {/* Luxury */}
        <div className="relative group cursor-pointer">
          <img
            src={luxuryImg}
            alt="Luxury Collection"
            className="rounded-xl shadow-lg transform group-hover:scale-105 transition"
          />
          <h3 className="absolute bottom-4 left-1/2 transform -translate-x-1/2 text-white font-bold bg-purple-600 px-4 py-2 rounded">
            Luxury Collection
          </h3>
        </div>
      </section>

      {/* See More Collection Button */}
      <div className="flex justify-center mb-12">
        <button
          onClick={() => navigate("/collections")}
          className="bg-pink-600 hover:bg-pink-700 text-white font-semibold px-8 py-3 rounded-lg transition"
        >
          See More Collection
        </button>
      </div>

      {/* Footer */}
      <footer className="bg-gray-800 text-white text-center py-6 mt-auto">
        <p>Â© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
