import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

// Images
import logo from "../assets/logo.jpg";
import hero from "../assets/hero.jpg";
import bridalImg from "../assets/bridal1.jpg";
import everydayImg from "../assets/everyday1.jpg";
import luxuryImg from "../assets/luxury1.jpg";

const Home = () => {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState("");
  const [products, setProducts] = useState([]); // MongoDB products

  // ðŸ”¹ MongoDB se unique products fetch karna
  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then((data) => {
        // ðŸ§  Duplicate name wale products hata do
        const unique = data.filter(
          (item, index, self) =>
            index === self.findIndex((t) => t.name === item.name)
        );
        setProducts(unique);
      })
      .catch((err) => console.error("Error fetching products:", err));
  }, []);

  const handleSearch = () => {
    if (searchTerm) {
      navigate(`/collections?search=${searchTerm}`);
    }
  };

  return (
    <div className="home-container">
      {/* Navbar */}
      <header className="navbar">
        <div className="logo">
          <img src={logo} alt="Elegant Jewelry Logo" className="logo-img" />
          <span className="brand-name">Elegant Jewelry</span>
        </div>
        <ul className="nav-links">
          <li><a href="/" className="active">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/collections">Collections</a></li>
          <li><a href="/orders">Orders</a></li>
          <li><a href="/wishlist">Wishlist</a></li>
          <li><a href="/contact">Contact</a></li>
        </ul>
      </header>

      {/* Hero */}
      <section className="hero">
        <img src={hero} alt="Hero Jewelry" className="hero-img" />
      </section>

      {/* Hero Panel */}
      <div className="hero-panel">
        <h1 className="hero-title">Elegant Jewelry</h1>
        <p className="hero-subtitle">
          Where timeless elegance meets modern design âœ¨
        </p>
        <p className="hero-subtitle">
          Discover our handpicked jewelry crafted for every occasion âœ¨
        </p>
        <button className="shop-btn" onClick={() => navigate("/collections")}>
          Explore Collection
        </button>
      </div>

      {/* Collections */}
      <section className="collections-section">
        <h2 className="collections-title">Our Collections</h2>
        <div className="collections-grid">
          <div className="collection-card">
            <img src={bridalImg} alt="Bridal Collection" />
            <div className="card-panel">
              <h3 className="collection-name">Bridal Collection</h3>
              <p className="collection-desc">
                Stunning jewelry for your special day â€” earrings, necklaces,
                rings, bracelets.
              </p>
              <button
                className="card-btn"
                onClick={() => navigate("/collections/bridal")}
              >
                Shop Now
              </button>
            </div>
          </div>

          <div className="collection-card">
            <img src={everydayImg} alt="Everyday Collection" />
            <div className="card-panel">
              <h3 className="collection-name">Everyday Collection</h3>
              <p className="collection-desc">
                Stylish and versatile pieces perfect for daily wear â€” lightweight,
                elegant, and timeless.
              </p>
              <button
                className="card-btn"
                onClick={() => navigate("/collections/everyday")}
              >
                Shop Now
              </button>
            </div>
          </div>

          <div className="collection-card">
            <img src={luxuryImg} alt="Luxury Collection" />
            <div className="card-panel">
              <h3 className="collection-name">Luxury Collection</h3>
              <p className="collection-desc">
                Exclusive and opulent jewelry for special occasions â€” crafted
                with premium materials and intricate designs.
              </p>
              <button
                className="card-btn"
                onClick={() => navigate("/collections/luxury")}
              >
                Shop Now
              </button>
            </div>
          </div>
        </div>
      </section>

      {/* ðŸŸ¢ Products from MongoDB */}
      <section className="products-section" style={{ padding: "40px", textAlign: "center" }}>
        <h2>New Arrivals</h2> {/* ðŸ”¥ Title changed */}
        {products.length === 0 ? (
          <p>Loading new jewelry...</p>
        ) : (
          <div
            style={{
              display: "flex",
              flexWrap: "wrap",
              justifyContent: "center",
              gap: "20px",
              marginTop: "20px",
            }}
          >
            {products.map((item) => (
              <div
                key={item._id}
                style={{
                  border: "1px solid #ccc",
                  borderRadius: "10px",
                  padding: "10px",
                  width: "200px",
                  textAlign: "center",
                }}
              >
                <img
                  src={item.image}
                  alt={item.name}
                  style={{
                    width: "100%",
                    height: "150px",
                    objectFit: "cover",
                    borderRadius: "8px",
                  }}
                />
                <h3>{item.name}</h3>
                <p>â‚¹{item.price}</p>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Footer */}
      <footer className="site-footer">
        <p>Â© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
