import React, { useState, useEffect } from "react";

const Orders = () => {
  const [cartItems, setCartItems] = useState([]);
  const [ordersPlaced, setOrdersPlaced] = useState([]);

  // Load items from localStorage
  useEffect(() => {
    const cart = JSON.parse(localStorage.getItem("cartItems")) || [];
    const placed = JSON.parse(localStorage.getItem("placedOrders")) || [];
    setCartItems(cart);
    setOrdersPlaced(placed);
  }, []);

  // Place order (Buy Now)
  const placeOrder = (item) => {
    const newOrders = [...ordersPlaced, item];
    setOrdersPlaced(newOrders);
    localStorage.setItem("placedOrders", JSON.stringify(newOrders));

    const newCart = cartItems.filter((c) => c.id !== item.id);
    setCartItems(newCart);
    localStorage.setItem("cartItems", JSON.stringify(newCart));
  };

  // Remove from cart
  const removeFromCart = (id) => {
    const newCart = cartItems.filter((c) => c.id !== id);
    setCartItems(newCart);
    localStorage.setItem("cartItems", JSON.stringify(newCart));
  };

  // Remove from orders
  const removeFromOrders = (id) => {
    const newOrders = ordersPlaced.filter((o) => o.id !== id);
    setOrdersPlaced(newOrders);
    localStorage.setItem("placedOrders", JSON.stringify(newOrders));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Cart Items</h2>
      {cartItems.length === 0 && <p>No items in cart.</p>}
      {cartItems.map((item) => (
        <div key={item.id} style={{ display: "flex", gap: "20px", margin: "10px 0", alignItems: "center" }}>
          <img src={item.img} alt={item.name} style={{ width: "60px", height: "60px", objectFit: "cover" }} />
          <span>{item.name}</span>
          <span>{item.price}</span>
          <button onClick={() => placeOrder(item)} style={{ padding: "5px 10px", backgroundColor: "green", color: "#fff", cursor: "pointer" }}>Buy Now</button>
          <button onClick={() => removeFromCart(item.id)} style={{ padding: "5px 10px", backgroundColor: "red", color: "#fff", cursor: "pointer" }}>Remove</button>
        </div>
      ))}

      <h2>Orders Placed</h2>
      {ordersPlaced.length === 0 && <p>No orders placed yet.</p>}
      {ordersPlaced.map((item) => (
        <div key={item.id} style={{ display: "flex", gap: "20px", margin: "10px 0", alignItems: "center" }}>
          <img src={item.img} alt={item.name} style={{ width: "60px", height: "60px", objectFit: "cover" }} />
          <span>{item.name}</span>
          <span>{item.price}</span>
          <button onClick={() => removeFromOrders(item.id)} style={{ padding: "5px 10px", backgroundColor: "red", color: "#fff", cursor: "pointer" }}>Remove</button>
        </div>
      ))}
    </div>
  );
};

export default Orders;
