import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

// Images
import logo from "../assets/logo.jpg";
import ring1 from "../assets/luxury-ring1.jpg";
import ring2 from "../assets/luxury-ring2.jpg";
import ring3 from "../assets/luxury-ring3.jpg";

const LuxuryRings = () => {
  const navigate = useNavigate();
  const [search, setSearch] = useState("");

  const rings = [
    { 
      name: "Silver Solitaire Diamond Ring", 
      img: ring1,
      desc: "Elegant silver ring featuring a single sparkling diamond at the center.",
      price: "₹60,000"
    },
    { 
      name: "Silver Multi-Stone Ring", 
      img: ring2,
      desc: "Sophisticated silver ring adorned with multiple silver and white stones for a shimmering look.",
      price: "₹75,000"
    },
    { 
      name: "Golden Diamond & White Stone Ring", 
      img: ring3,
      desc: "Luxury golden ring featuring white diamonds and a striking white nag centerpiece.",
      price: "₹1,20,000"
    },
  ];

  const filtered = rings.filter(item =>
    item.name.toLowerCase().includes(search.toLowerCase())
  );

  // Axios POST requests to backend
  const handleAddToCart = async (item) => {
    try {
      await axios.post("http://localhost:5000/cart", item);
      alert(`${item.name} added to Cart!`);
    } catch (err) {
      console.error(err);
      alert("Failed to add to Cart");
    }
  };

  const handleBuyNow = async (item) => {
    try {
      await axios.post("http://localhost:5000/orders", item);
      alert(`${item.name} added to Orders!`);
      navigate("/orders");
    } catch (err) {
      console.error(err);
      alert("Failed to place order");
    }
  };

  const handleWishlist = async (item) => {
    try {
      await axios.post("http://localhost:5000/wishlist", item);
      alert(`${item.name} added to Wishlist!`);
    } catch (err) {
      console.error(err);
      alert("Failed to add to Wishlist");
    }
  };

  return (
    <div className="home-container">
      {/* Header */}
      <header className="navbar">
        <div className="logo">
          <img src={logo} alt="Elegant Jewelry Logo" className="logo-img" />
          <span className="brand-name">Elegant Jewelry</span>
        </div>
        <ul className="nav-links">
          <li><a href="/">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/collections">Collections</a></li>
          <li><a href="/orders">Orders</a></li>
          <li><a href="/wishlist">Wishlist</a></li>
          <li><a href="/contact">Contact</a></li>
        </ul>
      </header>

      {/* Search Bar */}
      <div className="search-bar" style={{ textAlign: "center", margin: "40px 0 20px" }}>
        <input
          type="text"
          placeholder="Search Rings..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{
            padding: "10px 20px",
            borderRadius: "25px",
            border: "2px solid #d6336c",
            width: "260px",
            outline: "none",
          }}
        />
      </div>

      {/* Rings Grid */}
      <div className="collections-grid" style={{ display: "flex", justifyContent: "center", flexWrap: "wrap", gap: "25px" }}>
        {filtered.map((item, index) => (
          <div key={index} className="collection-card" style={{ border: "1px solid #ddd", borderRadius: "15px", width: "270px", padding: "15px", textAlign: "center" }}>
            <img src={item.img} alt={item.name} style={{ width: "100%", height: "230px", objectFit: "cover", borderRadius: "12px" }} />
            <h3>{item.name}</h3>
            <p style={{ fontSize: "14px", color: "#555" }}>{item.desc}</p>
            <p style={{ fontWeight: "bold", color: "#d6336c" }}>{item.price}</p>

            <div style={{ display: "flex", justifyContent: "center", gap: "10px", marginTop: "10px" }}>
              <button onClick={() => handleBuyNow(item)} style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 15px", borderRadius: "20px", border: "none", cursor: "pointer" }}>
                Buy Now
              </button>
              <button onClick={() => handleAddToCart(item)} style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 15px", borderRadius: "20px", border: "none", cursor: "pointer" }}>
                Add to Cart
              </button>
              <button onClick={() => handleWishlist(item)} style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 15px", borderRadius: "20px", border: "none", cursor: "pointer" }}>
                Wishlist
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Footer */}
      <footer style={{ backgroundColor: "#000", color: "#fff", textAlign: "center", padding: "20px 0", marginTop: "50px" }}>
        <p>© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default LuxuryRings;
