import React from "react";
import { useNavigate } from "react-router-dom";

// Images
import logo from "../assets/logo.jpg";
import hero from "../assets/hero.jpg"; // Your hero image
import bridalImg from "../assets/bridal1.jpg";
import everydayImg from "../assets/everyday1.jpg";
import luxuryImg from "../assets/luxury1.jpg";

const Home = () => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen flex flex-col">
      {/* Header */}
      <header className="flex items-center justify-between py-6 px-8 bg-white shadow-md z-50 relative">
        <div className="flex items-center">
          <img
            src={logo}
            alt="Elegant Jewelry Logo"
            className="h-20 w-20 object-contain mr-4"
          />
          <h1 className="text-4xl font-extrabold font-serif text-pink-700">
            Elegant Jewelry
          </h1>
        </div>
        <nav>
          <button
            onClick={() => navigate("/collections")}
            className="bg-pink-600 hover:bg-pink-700 text-white font-semibold px-6 py-2 rounded-lg transition"
          >
            Collections
          </button>
        </nav>
      </header>

      {/* Hero Section with Overlay */}
      <section
        className="relative w-full h-[80vh] flex items-center justify-center"
        style={{
          backgroundImage: `url(${hero})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        {/* Overlay */}
        <div className="absolute inset-0 bg-black bg-opacity-40"></div>

        {/* Hero Text */}
        <div className="relative text-center text-white px-4">
          <h2 className="text-5xl md:text-6xl font-bold mb-4">
            Welcome to Elegant Jewelry
          </h2>
          <p className="text-lg md:text-xl mb-6">
            Discover exquisite collections crafted for every occasion
          </p>
          <button
            onClick={() => navigate("/collections")}
            className="bg-pink-600 hover:bg-pink-700 text-white font-semibold px-8 py-3 rounded-lg transition"
          >
            Shop Now
          </button>
        </div>
      </section>

      {/* Collections Section */}
      <section className="flex flex-col md:flex-row justify-center items-center gap-6 my-12 px-4 md:px-20">
        {/* Bridal */}
        <div
          className="relative group cursor-pointer"
          onClick={() => navigate("/collections/bridal")}
        >
          <img
            src={bridalImg}
            alt="Bridal Collection"
            className="rounded-xl shadow-lg transform group-hover:scale-105 transition"
          />
          <h3 className="absolute bottom-4 left-1/2 transform -translate-x-1/2 text-white font-bold bg-pink-600 px-4 py-2 rounded">
            Bridal Collection
          </h3>
        </div>

        {/* Everyday */}
        <div
          className="relative group cursor-pointer"
          onClick={() => navigate("/collections/everyday")}
        >
          <img
            src={everydayImg}
            alt="Everyday Wear"
            className="rounded-xl shadow-lg transform group-hover:scale-105 transition"
          />
          <h3 className="absolute bottom-4 left-1/2 transform -translate-x-1/2 text-white font-bold bg-yellow-500 px-4 py-2 rounded">
            Everyday Wear
          </h3>
        </div>

        {/* Luxury */}
        <div
          className="relative group cursor-pointer"
          onClick={() => navigate("/collections/luxury")}
        >
          <img
            src={luxuryImg}
            alt="Luxury Collection"
            className="rounded-xl shadow-lg transform group-hover:scale-105 transition"
          />
          <h3 className="absolute bottom-4 left-1/2 transform -translate-x-1/2 text-white font-bold bg-purple-600 px-4 py-2 rounded">
            Luxury Collection
          </h3>
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-gray-800 text-white text-center py-6 mt-auto">
        <p>Â© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
