import React from "react";
import logo from "../assets/logo.jpg";
import aboutImg from "../assets/about.jpg"; // Your about page image

const About = () => {
  return (
    <div className="about-page">
      {/* Hero Image */}
      <section className="about-hero">
        <img src={aboutImg} alt="Elegant Jewelry" className="about-hero-img" />
      </section>

      {/* Panel below image */}
      <div className="about-panel">
        <h1 className="about-title">Elegant Jewelry</h1>
        <p className="about-tagline">Where timeless elegance meets modern craftsmanship ✨</p>
        <p className="about-desc">
          At Elegant Jewelry, we believe every piece tells a story. From intricate bridal designs to everyday elegance, we craft timeless jewelry that enhances your style and celebrates your moments.
        </p>
      </div>

      {/* Footer */}
      <footer className="footer">
        <p>© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default About;
