import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.jpg";

const Order = () => {
  const navigate = useNavigate();

  const [cartItems, setCartItems] = useState(() => JSON.parse(localStorage.getItem("cartItems")) || []);
  const [ordersPlaced, setOrdersPlaced] = useState(() => JSON.parse(localStorage.getItem("ordersPlaced")) || []);
  const [wishlistItems, setWishlistItems] = useState(() => JSON.parse(localStorage.getItem("wishlistItems")) || []);

  useEffect(() => {
    localStorage.setItem("cartItems", JSON.stringify(cartItems));
    localStorage.setItem("ordersPlaced", JSON.stringify(ordersPlaced));
    localStorage.setItem("wishlistItems", JSON.stringify(wishlistItems));
  }, [cartItems, ordersPlaced, wishlistItems]);

  const handlePlaceOrder = () => {
    const newOrders = [...ordersPlaced, ...cartItems];
    setOrdersPlaced(newOrders);
    setCartItems([]);
    alert("Order placed successfully!");
  };

  const handleRemoveItem = (type, index) => {
    if (type === "cart") {
      const updated = [...cartItems];
      updated.splice(index, 1);
      setCartItems(updated);
    } else if (type === "wishlist") {
      const updated = [...wishlistItems];
      updated.splice(index, 1);
      setWishlistItems(updated);
    }
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", minHeight: "100vh" }}>
      {/* Header */}
      <header className="navbar" style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "10px 20px", backgroundColor: "#f8f8f8" }}>
        <div className="logo" style={{ display: "flex", alignItems: "center" }}>
          <img src={logo} alt="Elegant Jewelry Logo" className="logo-img" style={{ height: "50px", marginRight: "10px" }} />
          <span className="brand-name" style={{ fontWeight: "bold", fontSize: "20px" }}>Elegant Jewelry</span>
        </div>
        <ul className="nav-links" style={{ display: "flex", listStyle: "none", gap: "15px" }}>
          <li><a href="/">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/collections">Collections</a></li>
          <li><a href="/orders" className="active">Orders</a></li>
          <li><a href="/wishlist">Wishlist</a></li>
          <li><a href="/contact">Contact</a></li>
        </ul>
      </header>

      {/* Content */}
      <div style={{ flex: "1", padding: "30px" }}>
        {/* Cart Items */}
        <h2>Cart Items</h2>
        {cartItems.length === 0 ? (
          <p>No items in Cart.</p>
        ) : (
          <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
            {cartItems.map((item, index) => (
              <div key={index} style={{ border: "1px solid #ddd", borderRadius: "10px", padding: "10px", width: "220px" }}>
                <img src={item.img} alt={item.name} style={{ width: "100%", height: "200px", objectFit: "cover", borderRadius: "10px" }} />
                <div style={{ padding: "10px 0" }}>
                  <h4 style={{ margin: "5px 0" }}>{item.name}</h4>
                  <p style={{ fontWeight: "bold", margin: "5px 0" }}>{item.price}</p>
                  <button style={{ backgroundColor: "#d6336c", color: "#fff", padding: "5px 10px", border: "none", borderRadius: "5px", cursor: "pointer" }} onClick={() => handleRemoveItem("cart", index)}>Remove</button>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Place Order Button */}
        {cartItems.length > 0 && (
          <div style={{ marginTop: "30px" }}>
            <button style={{ backgroundColor: "#28a745", color: "#fff", padding: "10px 20px", borderRadius: "10px", border: "none", cursor: "pointer", fontWeight: "bold" }} onClick={handlePlaceOrder}>
              Place Order
            </button>
          </div>
        )}

        {/* Orders Placed */}
        <h2 style={{ marginTop: "50px" }}>Orders Placed</h2>
        {ordersPlaced.length === 0 ? (
          <p>No orders placed yet.</p>
        ) : (
          <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
            {ordersPlaced.map((item, index) => (
              <div key={index} style={{ border: "1px solid #ddd", borderRadius: "10px", padding: "10px", width: "220px" }}>
                <img src={item.img} alt={item.name} style={{ width: "100%", height: "200px", objectFit: "cover", borderRadius: "10px" }} />
                <div style={{ padding: "10px 0" }}>
                  <h4 style={{ margin: "5px 0" }}>{item.name}</h4>
                  <p style={{ fontWeight: "bold", margin: "5px 0" }}>{item.price}</p>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Wishlist Items */}
        <h2 style={{ marginTop: "50px" }}>Wishlist Items</h2>
        {wishlistItems.length === 0 ? (
          <p>No items in Wishlist.</p>
        ) : (
          <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
            {wishlistItems.map((item, index) => (
              <div key={index} style={{ border: "1px solid #ddd", borderRadius: "10px", padding: "10px", width: "220px" }}>
                <img src={item.img} alt={item.name} style={{ width: "100%", height: "200px", objectFit: "cover", borderRadius: "10px" }} />
                <div style={{ padding: "10px 0" }}>
                  <h4 style={{ margin: "5px 0" }}>{item.name}</h4>
                  <p style={{ fontWeight: "bold", margin: "5px 0" }}>{item.price}</p>
                  <button style={{ backgroundColor: "#d6336c", color: "#fff", padding: "5px 10px", border: "none", borderRadius: "5px", cursor: "pointer" }} onClick={() => handleRemoveItem("wishlist", index)}>Remove</button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Footer */}
      <footer style={{ backgroundColor: "#000", color: "#fff", textAlign: "center", padding: "20px 0", marginTop: "auto" }}>
        <p>Â© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default Order;
