import React, { useState, useEffect } from "react";
import logo from "../assets/logo.jpg";

const Order = () => {
  // State to hold items
  const [cartItems, setCartItems] = useState([]);
  const [buyNowItems, setBuyNowItems] = useState([]);
  const [orderPlaced, setOrderPlaced] = useState([]);

  // Load from localStorage if using across pages
  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cartItems")) || [];
    const storedBuyNow = JSON.parse(localStorage.getItem("buyNowItems")) || [];
    const storedOrder = JSON.parse(localStorage.getItem("orderPlaced")) || [];
    setCartItems(storedCart);
    setBuyNowItems(storedBuyNow);
    setOrderPlaced(storedOrder);
  }, []);

  useEffect(() => {
    localStorage.setItem("cartItems", JSON.stringify(cartItems));
    localStorage.setItem("buyNowItems", JSON.stringify(buyNowItems));
    localStorage.setItem("orderPlaced", JSON.stringify(orderPlaced));
  }, [cartItems, buyNowItems, orderPlaced]);

  // Functions
  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  const buyNow = (item) => {
    setBuyNowItems([...buyNowItems, item]);
  };

  const removeItem = (item, type) => {
    if (type === "cart") setCartItems(cartItems.filter(i => i.name !== item.name));
    if (type === "buy") setBuyNowItems(buyNowItems.filter(i => i.name !== item.name));
    if (type === "order") setOrderPlaced(orderPlaced.filter(i => i.name !== item.name));
  };

  const placeOrder = () => {
    const allOrders = [...orderPlaced, ...buyNowItems, ...cartItems];
    setOrderPlaced(allOrders);
    setBuyNowItems([]);
    setCartItems([]);
    alert("Order placed successfully!");
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      {/* Header */}
      <header style={{ display: "flex", alignItems: "center", gap: "15px", marginBottom: "20px" }}>
        <img src={logo} alt="Elegant Jewelry" style={{ width: "60px" }} />
        <h2>Elegant Jewelry - Orders</h2>
      </header>

      {/* Cart Items */}
      <section style={{ marginBottom: "30px" }}>
        <h3>Cart Items</h3>
        {cartItems.length === 0 ? <p>No items in Cart.</p> : (
          <ul>
            {cartItems.map((item, index) => (
              <li key={index} style={{ marginBottom: "10px" }}>
                {item.name} - {item.price} 
                <button onClick={() => removeItem(item, "cart")} style={{ marginLeft: "10px" }}>Remove</button>
              </li>
            ))}
          </ul>
        )}
      </section>

      {/* Buy Now Items */}
      <section style={{ marginBottom: "30px" }}>
        <h3>Buy Now Items</h3>
        {buyNowItems.length === 0 ? <p>No Buy Now items.</p> : (
          <ul>
            {buyNowItems.map((item, index) => (
              <li key={index} style={{ marginBottom: "10px" }}>
                {item.name} - {item.price} 
                <button onClick={() => removeItem(item, "buy")} style={{ marginLeft: "10px" }}>Remove</button>
              </li>
            ))}
          </ul>
        )}
      </section>

      {/* Place Order Button */}
      {(cartItems.length > 0 || buyNowItems.length > 0) && (
        <button onClick={placeOrder} style={{ padding: "10px 20px", backgroundColor: "#d6336c", color: "#fff", border: "none", borderRadius: "20px", cursor: "pointer" }}>
          Place Order
        </button>
      )}

      {/* Orders Placed */}
      <section style={{ marginTop: "40px" }}>
        <h3>Orders Placed</h3>
        {orderPlaced.length === 0 ? <p>No orders placed yet.</p> : (
          <ul>
            {orderPlaced.map((item, index) => (
              <li key={index} style={{ marginBottom: "10px" }}>
                {item.name} - {item.price} 
                <button onClick={() => removeItem(item, "order")} style={{ marginLeft: "10px" }}>Remove</button>
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
};

export default Order;
