import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const DEV_EMAIL = "user@jewel.com";
const DEV_PASSWORD = "user1234";

const Login = ({ setIsLoggedIn }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    // DEV: simple default user (no backend)
    if (email === DEV_EMAIL && password === DEV_PASSWORD) {
      alert("✅ Logged in with dev account");
      const user = { email: DEV_EMAIL, name: "User", dev: true };
      localStorage.setItem("user", JSON.stringify(user));
      localStorage.setItem("isLoggedIn", "true");
      if (setIsLoggedIn) setIsLoggedIn(true);
      navigate("/");
      return;
    }

    // Normal backend login
    try {
      const res = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      const data = await res.json();

      if (res.ok) {
        alert("✅ " + (data.message || "Login successful"));
        const userObj = { email: data.email || email, name: data.name || "User", token: data.token || null };
        localStorage.setItem("user", JSON.stringify(userObj));
        localStorage.setItem("isLoggedIn", "true");
        if (setIsLoggedIn) setIsLoggedIn(true);
        navigate("/");
      } else {
        alert("❌ " + (data.message || "Login failed"));
      }
    } catch (err) {
      console.error(err);
      alert("❌ Server unreachable. Use dev account: user@jewel.com / user1234");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        /><br /><br />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        /><br /><br />
        <button type="submit">Login</button>
      </form>

      <p style={{ marginTop: "12px" }}>
        Dev login: <strong>user@jewel.com</strong> / <strong>user1234</strong>
      </p>

      <p>
        Don’t have an account?{" "}
        <span
          style={{ color: "blue", cursor: "pointer" }}
          onClick={() => navigate("/register")}
        >
          Register
        </span>
      </p>
    </div>
  );
};

export default Login;
