import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

// Images
import logo from "../assets/logo.jpg";
import ring1 from "../assets/luxury-ring1.jpg";
import ring2 from "../assets/luxury-ring2.jpg";
import ring3 from "../assets/luxury-ring3.jpg";

const LuxuryRings = () => {
  const navigate = useNavigate();
  const [search, setSearch] = useState("");

  const rings = [
    { name: "Silver Solitaire Diamond Ring", img: ring1, desc: "Elegant silver ring featuring a single sparkling diamond.", price: "₹60,000" },
    { name: "Silver Multi-Stone Ring", img: ring2, desc: "Silver ring with multiple silver & white stones.", price: "₹75,000" },
    { name: "Golden Diamond & White Stone Ring", img: ring3, desc: "Golden ring with white diamonds and a white nag centerpiece.", price: "₹1,20,000" },
  ];

  const filtered = rings.filter(item => item.name.toLowerCase().includes(search.toLowerCase()));

  const BASE_URL = "http://localhost:5000"; // Make sure backend is running here

  const handleAddToCart = async (item) => {
    try {
      const res = await fetch(`${BASE_URL}/cart`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(item),
      });
      const data = await res.json();
      alert(data.message);
    } catch (err) {
      console.error(err);
      alert("Failed to add to Cart");
    }
  };

  const handleBuyNow = async (item) => {
    try {
      const res = await fetch(`${BASE_URL}/orders`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(item),
      });
      const data = await res.json();
      alert(data.message);
      navigate("/orders");
    } catch (err) {
      console.error(err);
      alert("Failed to place order");
    }
  };

  const handleWishlist = async (item) => {
    try {
      const res = await fetch(`${BASE_URL}/wishlist`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(item),
      });
      const data = await res.json();
      alert(data.message);
    } catch (err) {
      console.error(err);
      alert("Failed to add to Wishlist");
    }
  };

  return (
    <div className="home-container">
      <header className="navbar">
        <div className="logo">
          <img src={logo} alt="Elegant Jewelry Logo" className="logo-img" />
          <span className="brand-name">Elegant Jewelry</span>
        </div>
        <ul className="nav-links">
          <li><a href="/">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/collections">Collections</a></li>
          <li><a href="/orders">Orders</a></li>
          <li><a href="/wishlist">Wishlist</a></li>
          <li><a href="/contact">Contact</a></li>
        </ul>
      </header>

      <div className="search-bar" style={{ textAlign: "center", margin: "40px 0" }}>
        <input
          type="text"
          placeholder="Search Rings..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{ padding: "10px 20px", borderRadius: "25px", border: "2px solid #d6336c", width: "260px" }}
        />
      </div>

      <section className="collections-section" style={{ textAlign: "center" }}>
        <h2 style={{ marginBottom: "30px" }}>Luxury Rings</h2>
        <div style={{ display: "flex", justifyContent: "center", flexWrap: "wrap", gap: "25px" }}>
          {filtered.map((item, index) => (
            <div key={index} style={{ border: "1px solid #ddd", borderRadius: "15px", width: "270px", padding: "15px", textAlign: "center" }}>
              <img src={item.img} alt={item.name} style={{ width: "100%", height: "230px", borderRadius: "12px", objectFit: "cover" }} />
              <h3>{item.name}</h3>
              <p>{item.desc}</p>
              <p style={{ fontWeight: "bold", color: "#d6336c" }}>{item.price}</p>

              <div style={{ display: "flex", justifyContent: "center", gap: "10px" }}>
                <button onClick={() => handleBuyNow(item)} style={{ backgroundColor: "#d6336c", color: "#fff", border: "none", borderRadius: "20px", padding: "8px 15px" }}>Buy Now</button>
                <button onClick={() => handleAddToCart(item)} style={{ backgroundColor: "#d6336c", color: "#fff", border: "none", borderRadius: "20px", padding: "8px 15px" }}>Add to Cart</button>
                <button onClick={() => handleWishlist(item)} style={{ backgroundColor: "#d6336c", color: "#fff", border: "none", borderRadius: "20px", padding: "8px 15px" }}>Wishlist</button>
              </div>
            </div>
          ))}
        </div>
      </section>

      <footer style={{ backgroundColor: "#000", color: "#fff", textAlign: "center", padding: "20px 0", marginTop: "50px" }}>
        © 2025 Elegant Jewelry. All Rights Reserved.
      </footer>
    </div>
  );
};

export default LuxuryRings;
