import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

// Images
import logo from "../assets/logo.jpg";
import necklace1 from "../assets/bridal-set1.jpg"; // Red & Gold
import necklace2 from "../assets/bridal-set2.jpg"; // White & Green
import necklace3 from "../assets/bridal-set3.jpg"; // Pearl

const BridalNecklace = () => {
  const navigate = useNavigate();
  const [search, setSearch] = useState("");
  const [wishlist, setWishlist] = useState([]);

  const necklaces = [
    { 
      id: 1,
      name: "Royal Red & Gold Necklace", 
      img: necklace1,
      desc: "Intricately designed golden necklace adorned with deep red stones, perfect for a regal bridal look.",
      price: "₹45,000"
    },
    { 
      id: 2,
      name: "Emerald & Pearl White Necklace", 
      img: necklace2,
      desc: "Elegant white beads with green accents, combining purity and tradition for your special day.",
      price: "₹38,000"
    },
    { 
      id: 3,
      name: "Classic Pearl Bridal Necklace", 
      img: necklace3,
      desc: "Timeless pearl necklace with a sophisticated design, ideal for a graceful bridal appearance.",
      price: "₹50,000"
    },
  ];

  // Load wishlist from localStorage
  useEffect(() => {
    const savedWishlist = JSON.parse(localStorage.getItem("wishlist")) || [];
    setWishlist(savedWishlist);
  }, []);

  // Save wishlist to localStorage
  useEffect(() => {
    localStorage.setItem("wishlist", JSON.stringify(wishlist));
  }, [wishlist]);

  const addToCart = (item) => {
    let cart = JSON.parse(localStorage.getItem("cart")) || [];
    cart.push(item);
    localStorage.setItem("cart", JSON.stringify(cart));
    alert(`${item.name} added to Cart!`);
  };

  const buyNow = (item) => {
    let buyNowItems = JSON.parse(localStorage.getItem("buyNow")) || [];
    buyNowItems.push(item);
    localStorage.setItem("buyNow", JSON.stringify(buyNowItems));
    alert(`Buying: ${item.name}`);
    navigate("/orders"); // redirect to Orders page
  };

  const toggleWishlist = (item) => {
    if (wishlist.find((w) => w.id === item.id)) {
      const updated = wishlist.filter((w) => w.id !== item.id);
      setWishlist(updated);
    } else {
      setWishlist([...wishlist, item]);
    }
  };

  const filtered = necklaces.filter((item) =>
    item.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="home-container">
      {/* Header */}
      <header className="navbar">
        <div className="logo">
          <img src={logo} alt="Elegant Jewelry Logo" className="logo-img" />
          <span className="brand-name">Elegant Jewelry</span>
        </div>
        <ul className="nav-links">
          <li><a href="/">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/collections">Collections</a></li>
          <li><a href="/orders">Orders</a></li>
          <li><a href="/wishlist">Wishlist</a></li>
          <li><a href="/contact">Contact</a></li>
        </ul>
      </header>

      {/* Search Bar */}
      <div className="search-bar" style={{ textAlign: "center", margin: "40px 0 20px 0" }}>
        <input
          type="text"
          placeholder="Search Necklace..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{
            padding: "10px 20px",
            borderRadius: "25px",
            border: "2px solid #d6336c",
            width: "260px",
            outline: "none",
            fontSize: "15px",
          }}
        />
      </div>

      {/* Necklace Section */}
      <section className="collections-section">
        <h2 className="collections-title">Bridal Necklaces</h2>
        <div className="collections-grid">
          {filtered.map((item) => (
            <div key={item.id} className="collection-card">
              <img src={item.img} alt={item.name} />
              <div className="card-panel">
                <h3 className="collection-name">{item.name}</h3>
                <p className="collection-desc">{item.desc}</p>
                <p className="collection-price" style={{ fontWeight: "bold", marginBottom: "10px" }}>
                  {item.price}
                </p>

                {/* Buttons Row */}
                <div className="button-row" style={{ display: "flex", gap: "10px", marginBottom: "10px" }}>
                  <button 
                    className="buy-btn"
                    onClick={() => buyNow(item)}
                    style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 15px", borderRadius: "20px", border: "none", cursor: "pointer" }}
                  >
                    Buy Now
                  </button>
                  <button 
                    className="addcart-btn"
                    onClick={() => addToCart(item)}
                    style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 15px", borderRadius: "20px", border: "none", cursor: "pointer" }}
                  >
                    Add to Cart
                  </button>
                </div>

                {/* Wishlist */}
                <div className="wishlist-row">
                  <button 
                    className="wishlist-btn"
                    onClick={() => toggleWishlist(item)}
                    style={{ backgroundColor: wishlist.find(w => w.id === item.id) ? "#ffa500" : "#d6336c", color: "#fff", padding: "8px 20px", borderRadius: "20px", border: "none", cursor: "pointer" }}
                  >
                    {wishlist.find(w => w.id === item.id) ? "Remove from Wishlist" : "Add to Wishlist"}
                  </button>
                </div>

              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer className="site-footer" style={{ backgroundColor: "#000", color: "#fff", padding: "20px 0", textAlign: "center", marginTop: "50px" }}>
        <p>© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default BridalNecklace;
