import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.jpg";
const BASE_URL = "http://localhost:5000";

const Wishlist = () => {
  const navigate = useNavigate();
  const [wishlistItems, setWishlistItems] = useState([]);

  useEffect(() => {
    fetch(`${BASE_URL}/wishlist`).then(res => res.json()).then(setWishlistItems).catch(console.error);
  }, []);

  const handleAddToCart = async (item) => {
    await fetch(`${BASE_URL}/cart`, { method: "POST", headers: { "Content-Type": "application/json" }, body: JSON.stringify(item) });
    alert(`${item.name} added to Cart!`);
  };

  const handleBuyNow = async (item) => {
    await fetch(`${BASE_URL}/orders`, { method: "POST", headers: { "Content-Type": "application/json" }, body: JSON.stringify(item) });
    await fetch(`${BASE_URL}/wishlist`, { method: "POST", headers: { "Content-Type": "application/json" }, body: JSON.stringify({ remove: item.name }) });
    setWishlistItems(prev => prev.filter(i => i.name !== item.name));
    alert(`${item.name} moved to Orders!`);
    navigate("/orders");
  };

  const handleRemove = async (name) => {
    await fetch(`${BASE_URL}/wishlist/${name}`, { method: "DELETE" });
    setWishlistItems(prev => prev.filter(i => i.name !== name));
    alert(`${name} removed from Wishlist`);
  };

  return (
    <div>
      <header style={{ display: "flex", justifyContent: "space-between", padding: "15px 30px", background: "#f8f8f8" }}>
        <img src={logo} alt="logo" style={{ height: "50px" }} />
      </header>

      <h2>Wishlist Items</h2>
      <div style={{ display: "flex", gap: "20px", flexWrap: "wrap" }}>
        {wishlistItems.map((item, idx) => (
          <div key={idx} style={{ border: "1px solid #ddd", padding: "10px", width: "220px", textAlign: "center" }}>
            <img src={item.img} alt={item.name} style={{ width: "100%", height: "180px", objectFit: "cover" }} />
            <h4>{item.name}</h4>
            <p style={{ fontWeight: "bold" }}>{item.price}</p>
            <div>
              <button onClick={() => handleBuyNow(item)}>Buy Now</button>
              <button onClick={() => handleAddToCart(item)}>Add to Cart</button>
              <button onClick={() => handleRemove(item.name)}>Remove</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Wishlist;
