import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

// Images
import logo from "../assets/logo.jpg";
import bracelet1 from "../assets/luxury-bracelet1.jpg"; // Pink golden-ish
import bracelet2 from "../assets/luxury-bracelet2.jpg"; // Golden lock, bee-hive
import bracelet3 from "../assets/luxury-bracelet3.jpg"; // Red ruby + diamond golden chain

const LuxuryBracelets = () => {
  const navigate = useNavigate();
  const [search, setSearch] = useState("");

  const bracelets = [
    { 
      name: "Pink Golden Luxury Bracelet", 
      img: bracelet1,
      desc: "Elegant bracelet with pink golden hues, adding a luxurious touch to your ensemble.",
      price: "₹1,25,000"
    },
    { 
      name: "Golden Lock Bee-Hive Bracelet", 
      img: bracelet2,
      desc: "Unique golden bracelet with a lock and bee-hive inspired design, perfect for statement elegance.",
      price: "₹95,000"
    },
    { 
      name: "Red Ruby & Diamond Golden Bracelet", 
      img: bracelet3,
      desc: "Luxury golden bracelet adorned with sparkling diamonds and a vibrant red ruby centerpiece.",
      price: "₹1,50,000"
    },
  ];

  // Helper functions for localStorage
  const addToCart = (item) => {
    const cart = JSON.parse(localStorage.getItem("cart")) || [];
    cart.push(item);
    localStorage.setItem("cart", JSON.stringify(cart));
    alert(`${item.name} added to cart!`);
  };

  const addToWishlist = (item) => {
    const wishlist = JSON.parse(localStorage.getItem("wishlist")) || [];
    wishlist.push(item);
    localStorage.setItem("wishlist", JSON.stringify(wishlist));
    alert(`${item.name} added to wishlist!`);
  };

  const buyNow = (item) => {
    const orders = JSON.parse(localStorage.getItem("orders")) || [];
    orders.push({ ...item, status: "Order Placed" });
    localStorage.setItem("orders", JSON.stringify(orders));
    alert(`Order placed for: ${item.name}`);
    navigate("/orders");
  };

  const filtered = bracelets.filter((item) =>
    item.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="home-container">
      {/* Header */}
      <header className="navbar">
        <div className="logo">
          <img src={logo} alt="Elegant Jewelry Logo" className="logo-img" />
          <span className="brand-name">Elegant Jewelry</span>
        </div>
        <ul className="nav-links">
          <li><a href="/">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/collections">Collections</a></li>
          <li><a href="/orders">Orders</a></li>
          <li><a href="/wishlist">Wishlist</a></li>
          <li><a href="/contact">Contact</a></li>
        </ul>
      </header>

      {/* Search Bar */}
      <div
        className="search-bar"
        style={{ textAlign: "center", margin: "40px 0 20px 0" }}
      >
        <input
          type="text"
          placeholder="Search Bracelets..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{
            padding: "10px 20px",
            borderRadius: "25px",
            border: "2px solid #d6336c",
            width: "260px",
            outline: "none",
            fontSize: "15px",
          }}
        />
        <button
          style={{
            marginLeft: "10px",
            padding: "10px 25px",
            borderRadius: "25px",
            border: "none",
            backgroundColor: "#d6336c",
            color: "white",
            fontWeight: "500",
            cursor: "pointer",
          }}
          onClick={() => alert(`Searching for: ${search}`)}
        >
          Search
        </button>
      </div>

      {/* Bracelets Section */}
      <section className="collections-section">
        <h2 className="collections-title">Luxury Bracelets</h2>
        <div className="collections-grid">
          {filtered.map((item, index) => (
            <div key={index} className="collection-card">
              <img src={item.img} alt={item.name} />
              <div className="card-panel">
                <h3 className="collection-name">{item.name}</h3>
                <p className="collection-desc">{item.desc}</p>
                <p
                  className="collection-price"
                  style={{ fontWeight: "bold", marginBottom: "10px" }}
                >
                  {item.price}
                </p>

                {/* Buttons Row */}
                <div
                  className="button-row"
                  style={{
                    display: "flex",
                    gap: "10px",
                    marginBottom: "10px",
                  }}
                >
                  <button
                    className="buy-btn"
                    onClick={() => buyNow(item)}
                    style={{
                      backgroundColor: "#d6336c",
                      color: "#fff",
                      padding: "8px 15px",
                      borderRadius: "20px",
                      border: "none",
                      cursor: "pointer",
                    }}
                  >
                    Buy Now
                  </button>
                  <button
                    className="addcart-btn"
                    onClick={() => addToCart(item)}
                    style={{
                      backgroundColor: "#d6336c",
                      color: "#fff",
                      padding: "8px 15px",
                      borderRadius: "20px",
                      border: "none",
                      cursor: "pointer",
                    }}
                  >
                    Add to Cart
                  </button>
                </div>

                <div className="wishlist-row">
                  <button
                    className="wishlist-btn"
                    onClick={() => addToWishlist(item)}
                    style={{
                      backgroundColor: "#d6336c",
                      color: "#fff",
                      padding: "8px 20px",
                      borderRadius: "20px",
                      border: "none",
                      cursor: "pointer",
                    }}
                  >
                    Wishlist
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer
        className="site-footer"
        style={{
          backgroundColor: "#000",
          color: "#fff",
          padding: "20px 0",
          textAlign: "center",
          marginTop: "50px",
        }}
      >
        <p>© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default LuxuryBracelets;
