import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.jpg";

const Order = () => {
  const navigate = useNavigate();

  const [cartItems, setCartItems] = useState(() => JSON.parse(localStorage.getItem("cartItems")) || []);
  const [buyNowItems, setBuyNowItems] = useState(() => JSON.parse(localStorage.getItem("buyNowItems")) || []);
  const [ordersPlaced, setOrdersPlaced] = useState(() => JSON.parse(localStorage.getItem("ordersPlaced")) || []);
  const [wishlistItems, setWishlistItems] = useState(() => JSON.parse(localStorage.getItem("wishlistItems")) || []);

  useEffect(() => {
    localStorage.setItem("cartItems", JSON.stringify(cartItems));
    localStorage.setItem("buyNowItems", JSON.stringify(buyNowItems));
    localStorage.setItem("ordersPlaced", JSON.stringify(ordersPlaced));
    localStorage.setItem("wishlistItems", JSON.stringify(wishlistItems));
  }, [cartItems, buyNowItems, ordersPlaced, wishlistItems]);

  const handlePlaceOrder = () => {
    const newOrders = [...ordersPlaced, ...cartItems, ...buyNowItems];
    setOrdersPlaced(newOrders);
    setCartItems([]);
    setBuyNowItems([]);
    alert("Order placed successfully!");
  };

  const handleRemoveItem = (type, index) => {
    if (type === "cart") {
      const updated = [...cartItems];
      updated.splice(index, 1);
      setCartItems(updated);
    } else if (type === "buyNow") {
      const updated = [...buyNowItems];
      updated.splice(index, 1);
      setBuyNowItems(updated);
    } else if (type === "wishlist") {
      const updated = [...wishlistItems];
      updated.splice(index, 1);
      setWishlistItems(updated);
    }
  };

  return (
    <div className="order-page" style={{ display: "flex", flexDirection: "column", minHeight: "100vh" }}>
      {/* Header */}
      <header className="navbar">
        <div className="logo" style={{ display: "flex", alignItems: "center" }}>
          <img src={logo} alt="Elegant Jewelry Logo" className="logo-img" />
          <span className="brand-name">Elegant Jewelry</span>
        </div>
        <ul className="nav-links">
          <li><a href="/">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/collections">Collections</a></li>
          <li><a href="/orders" className="active">Orders</a></li>
          <li><a href="/wishlist">Wishlist</a></li>
          <li><a href="/contact">Contact</a></li>
        </ul>
      </header>

      {/* Content */}
      <div className="order-content" style={{ padding: "30px", flex: "1" }}>
        {/* Cart Items */}
        <h2>Cart Items</h2>
        {cartItems.length === 0 ? (
          <p>No items in Cart.</p>
        ) : (
          <div className="item-list">
            {cartItems.map((item, index) => (
              <div key={index} className="order-card">
                <img src={item.img} alt={item.name} className="order-img" />
                <div className="order-info">
                  <h4>{item.name}</h4>
                  <p style={{ fontWeight: "bold" }}>{item.price}</p>
                </div>
                <button className="remove-btn" onClick={() => handleRemoveItem("cart", index)}>Remove</button>
              </div>
            ))}
          </div>
        )}

        {/* Buy Now Items */}
        <h2 style={{ marginTop: "40px" }}>Buy Now Items</h2>
        {buyNowItems.length === 0 ? (
          <p>No Buy Now items.</p>
        ) : (
          <div className="item-list">
            {buyNowItems.map((item, index) => (
              <div key={index} className="order-card">
                <img src={item.img} alt={item.name} className="order-img" />
                <div className="order-info">
                  <h4>{item.name}</h4>
                  <p style={{ fontWeight: "bold" }}>{item.price}</p>
                </div>
                <button className="remove-btn" onClick={() => handleRemoveItem("buyNow", index)}>Remove</button>
              </div>
            ))}
          </div>
        )}

        {/* Place Order Button */}
        {(cartItems.length > 0 || buyNowItems.length > 0) && (
          <div style={{ marginTop: "30px" }}>
            <button className="place-order-btn" onClick={handlePlaceOrder}>
              Place Order
            </button>
          </div>
        )}

        {/* Orders Placed */}
        <h2 style={{ marginTop: "50px" }}>Orders Placed</h2>
        {ordersPlaced.length === 0 ? (
          <p>No orders placed yet.</p>
        ) : (
          <div className="item-list">
            {ordersPlaced.map((item, index) => (
              <div key={index} className="order-card">
                <img src={item.img} alt={item.name} className="order-img" />
                <div className="order-info">
                  <h4>{item.name}</h4>
                  <p style={{ fontWeight: "bold" }}>{item.price}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Footer fixed at bottom */}
      <footer className="site-footer" style={{ backgroundColor: "#000", color: "#fff", textAlign: "center", padding: "20px 0" }}>
        <p>Â© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default Order;
