import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.jpg";

const Order = () => {
  const navigate = useNavigate();

  // States linked to localStorage
  const [cartItems, setCartItems] = useState(() => JSON.parse(localStorage.getItem("cartItems")) || []);
  const [ordersPlaced, setOrdersPlaced] = useState(() => JSON.parse(localStorage.getItem("ordersPlaced")) || []);
  const [wishlistItems, setWishlistItems] = useState(() => JSON.parse(localStorage.getItem("wishlistItems")) || []);

  // Persist to localStorage
  useEffect(() => {
    localStorage.setItem("cartItems", JSON.stringify(cartItems));
    localStorage.setItem("ordersPlaced", JSON.stringify(ordersPlaced));
    localStorage.setItem("wishlistItems", JSON.stringify(wishlistItems));
  }, [cartItems, ordersPlaced, wishlistItems]);

  // Place order from cart
  const handlePlaceOrder = () => {
    if (cartItems.length === 0) return alert("No items in cart to place order.");
    setOrdersPlaced([...ordersPlaced, ...cartItems]);
    setCartItems([]);
    alert("Order placed successfully!");
  };

  // Buy now from wishlist
  const handleBuyNowFromWishlist = (index) => {
    const item = wishlistItems[index];
    setOrdersPlaced([...ordersPlaced, item]);
    const updated = [...wishlistItems];
    updated.splice(index, 1);
    setWishlistItems(updated);
    alert(`${item.name} purchased successfully!`);
  };

  // Remove item
  const handleRemoveItem = (type, index) => {
    if (type === "cart") {
      const updated = [...cartItems];
      updated.splice(index, 1);
      setCartItems(updated);
    } else if (type === "wishlist") {
      const updated = [...wishlistItems];
      updated.splice(index, 1);
      setWishlistItems(updated);
    }
  };

  // Card styling
  const cardStyle = {
    border: "1px solid #ddd",
    borderRadius: "10px",
    padding: "10px",
    width: "220px",
  };

  const imgStyle = {
    width: "100%",
    height: "180px",
    objectFit: "cover",
    borderRadius: "10px",
  };

  const btnStyle = {
    backgroundColor: "#d6336c",
    color: "#fff",
    padding: "5px 10px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    marginRight: "5px",
  };

  const placeOrderBtn = {
    backgroundColor: "#28a745",
    color: "#fff",
    padding: "10px 20px",
    borderRadius: "10px",
    border: "none",
    cursor: "pointer",
    fontWeight: "bold",
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", minHeight: "100vh" }}>
      {/* Header */}
      <header className="navbar" style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "10px 20px", backgroundColor: "#f8f8f8" }}>
        <div className="logo" style={{ display: "flex", alignItems: "center" }}>
          <img src={logo} alt="Elegant Jewelry Logo" style={{ height: "50px", marginRight: "10px" }} />
          <span style={{ fontWeight: "bold", fontSize: "20px" }}>Elegant Jewelry</span>
        </div>
        <ul style={{ display: "flex", listStyle: "none", gap: "15px" }}>
          <li><a href="/">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/collections">Collections</a></li>
          <li><a href="/orders" className="active">Orders</a></li>
          <li><a href="/wishlist">Wishlist</a></li>
          <li><a href="/contact">Contact</a></li>
        </ul>
      </header>

      {/* Content */}
      <div style={{ flex: "1", padding: "30px" }}>
        {/* Cart Items */}
        <h2>Cart Items</h2>
        {cartItems.length === 0 ? <p>No items in Cart.</p> :
          <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
            {cartItems.map((item, index) => (
              <div key={index} style={cardStyle}>
                <img src={item.img} alt={item.name} style={imgStyle} />
                <h4>{item.name}</h4>
                <p style={{ fontWeight: "bold" }}>{item.price}</p>
                <button style={btnStyle} onClick={() => handleRemoveItem("cart", index)}>Remove</button>
              </div>
            ))}
          </div>
        }

        {/* Place Order Button */}
        {cartItems.length > 0 && (
          <div style={{ marginTop: "20px" }}>
            <button style={placeOrderBtn} onClick={handlePlaceOrder}>Place Order</button>
          </div>
        )}

        {/* Orders Placed */}
        <h2 style={{ marginTop: "50px" }}>Orders Placed</h2>
        {ordersPlaced.length === 0 ? <p>No orders placed yet.</p> :
          <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
            {ordersPlaced.map((item, index) => (
              <div key={index} style={cardStyle}>
                <img src={item.img} alt={item.name} style={imgStyle} />
                <h4>{item.name}</h4>
                <p style={{ fontWeight: "bold" }}>{item.price}</p>
              </div>
            ))}
          </div>
        }

        {/* Wishlist Items */}
        <h2 style={{ marginTop: "50px" }}>Wishlist Items</h2>
        {wishlistItems.length === 0 ? <p>No items in Wishlist.</p> :
          <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
            {wishlistItems.map((item, index) => (
              <div key={index} style={cardStyle}>
                <img src={item.img} alt={item.name} style={imgStyle} />
                <h4>{item.name}</h4>
                <p style={{ fontWeight: "bold" }}>{item.price}</p>
                <button style={btnStyle} onClick={() => handleBuyNowFromWishlist(index)}>Buy Now</button>
                <button style={btnStyle} onClick={() => handleRemoveItem("wishlist", index)}>Remove</button>
              </div>
            ))}
          </div>
        }
      </div>

      {/* Footer */}
      <footer style={{ backgroundColor: "#000", color: "#fff", textAlign: "center", padding: "20px 0", marginTop: "auto" }}>
        <p>Â© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default Order;
