import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.jpg";

const Orders = () => {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [buyNowItems, setBuyNowItems] = useState([]);

  // Load items from localStorage
  useEffect(() => {
    setCartItems(JSON.parse(localStorage.getItem("cart")) || []);
    setBuyNowItems(JSON.parse(localStorage.getItem("buyNow")) || []);
  }, []);

  const removeItem = (id, type) => {
    if (type === "cart") {
      const updated = cartItems.filter(item => item.id !== id);
      setCartItems(updated);
      localStorage.setItem("cart", JSON.stringify(updated));
    } else if (type === "buyNow") {
      const updated = buyNowItems.filter(item => item.id !== id);
      setBuyNowItems(updated);
      localStorage.setItem("buyNow", JSON.stringify(updated));
    }
  };

  const placeOrder = () => {
    if (cartItems.length === 0 && buyNowItems.length === 0) {
      alert("No items to place order!");
      return;
    }

    alert("Order placed successfully!");
    setCartItems([]);
    setBuyNowItems([]);
    localStorage.removeItem("cart");
    localStorage.removeItem("buyNow");
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", paddingBottom: "50px" }}>
      {/* Header */}
      <header style={{ backgroundColor: "#d6336c", color: "#fff", padding: "15px 20px", display: "flex", alignItems: "center", justifyContent: "space-between" }}>
        <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
          <img src={logo} alt="Elegant Jewelry Logo" style={{ width: "50px" }} />
          <h2>Elegant Jewelry - Orders</h2>
        </div>
        <div>
          <button onClick={() => navigate("/collections")} style={{ marginRight: "10px", padding: "8px 15px", borderRadius: "10px", border: "none", cursor: "pointer" }}>Collections</button>
          <button onClick={() => navigate("/wishlist")} style={{ padding: "8px 15px", borderRadius: "10px", border: "none", cursor: "pointer" }}>Wishlist</button>
        </div>
      </header>

      <div style={{ maxWidth: "900px", margin: "30px auto", padding: "0 20px" }}>
        {/* Cart Items */}
        <section style={{ marginBottom: "30px" }}>
          <h3 style={{ borderBottom: "2px solid #d6336c", paddingBottom: "5px" }}>Cart Items</h3>
          {cartItems.length === 0 ? (
            <p>No items in Cart.</p>
          ) : (
            cartItems.map(item => (
              <div key={item.id} style={{ display: "flex", gap: "20px", alignItems: "center", marginBottom: "15px", border: "1px solid #ccc", padding: "10px", borderRadius: "10px" }}>
                <img src={item.img} alt={item.name} style={{ width: "80px", borderRadius: "5px" }} />
                <div style={{ flex: 1 }}>
                  <p style={{ fontWeight: "bold", margin: "5px 0" }}>{item.name}</p>
                  <p style={{ color: "#d6336c" }}>{item.price}</p>
                </div>
                <button onClick={() => removeItem(item.id, "cart")} style={{ padding: "5px 10px", backgroundColor: "#f44336", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer" }}>Remove</button>
              </div>
            ))
          )}
        </section>

        {/* Buy Now Items */}
        <section style={{ marginBottom: "30px" }}>
          <h3 style={{ borderBottom: "2px solid #d6336c", paddingBottom: "5px" }}>Buy Now Items</h3>
          {buyNowItems.length === 0 ? (
            <p>No Buy Now items.</p>
          ) : (
            buyNowItems.map(item => (
              <div key={item.id} style={{ display: "flex", gap: "20px", alignItems: "center", marginBottom: "15px", border: "1px solid #ccc", padding: "10px", borderRadius: "10px" }}>
                <img src={item.img} alt={item.name} style={{ width: "80px", borderRadius: "5px" }} />
                <div style={{ flex: 1 }}>
                  <p style={{ fontWeight: "bold", margin: "5px 0" }}>{item.name}</p>
                  <p style={{ color: "#d6336c" }}>{item.price}</p>
                </div>
                <button onClick={() => removeItem(item.id, "buyNow")} style={{ padding: "5px 10px", backgroundColor: "#f44336", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer" }}>Remove</button>
              </div>
            ))
          )}
        </section>

        {/* Place Order */}
        <div style={{ textAlign: "center", marginTop: "20px" }}>
          <button onClick={placeOrder} style={{ padding: "12px 25px", backgroundColor: "#d6336c", color: "#fff", border: "none", borderRadius: "10px", fontSize: "16px", cursor: "pointer" }}>
            Place Order
          </button>
        </div>
      </div>
    </div>
  );
};

export default Orders;
