import React from "react";
import { useNavigate } from "react-router-dom";

// Images
import logo from "../assets/logo.jpg";
import hero from "../assets/hero.jpg";
import bridalImg from "../assets/bridal1.jpg";
import everydayImg from "../assets/everyday1.jpg";
import luxuryImg from "../assets/luxury1.jpg";

const Home = () => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen flex flex-col bg-white">
      {/* Header */}
      <header className="flex items-center justify-between py-4 px-8 bg-white shadow-md sticky top-0 z-50">
        <div className="flex items-center">
          <img
            src={logo}
            alt="Elegant Jewelry Logo"
            className="h-16 w-16 object-cover rounded-full mr-3"
          />
          <h1 className="text-3xl md:text-4xl font-serif font-bold text-pink-700">
            Elegant Jewelry
          </h1>
        </div>
        <nav>
          <button
            onClick={() => navigate("/collections")}
            className="bg-pink-600 hover:bg-pink-700 text-white font-semibold px-6 py-2 rounded-lg transition"
          >
            Collections
          </button>
        </nav>
      </header>

      {/* Hero Section */}
      <section className="relative w-full max-h-[450px] overflow-hidden mt-4">
        <img
          src={hero}
          alt="Hero Jewelry"
          className="w-full h-[450px] object-cover object-center rounded-xl"
        />
        <div className="absolute inset-0 bg-black bg-opacity-40"></div>
        <div className="absolute inset-0 flex flex-col items-center justify-center text-center text-white px-4">
          <h2 className="text-4xl md:text-5xl font-bold mb-2">
            Welcome to Elegant Jewelry
          </h2>
          <p className="text-lg md:text-xl mb-4">
            Discover exquisite collections crafted for every occasion
          </p>
          <button
            onClick={() => navigate("/collections")}
            className="bg-pink-600 hover:bg-pink-700 text-white font-semibold px-8 py-3 rounded-lg transition"
          >
            Shop Now
          </button>
        </div>
      </section>

      {/* Collections Section */}
      <section className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8 my-16 px-8 md:px-20">
        {/* Bridal */}
        <div
          className="relative group cursor-pointer"
          onClick={() => navigate("/collections/bridal")}
        >
          <img
            src={bridalImg}
            alt="Bridal Collection"
            className="rounded-xl shadow-lg h-64 w-full object-cover transform group-hover:scale-105 transition duration-300"
          />
          <h3 className="absolute bottom-4 left-1/2 transform -translate-x-1/2 text-white font-semibold bg-pink-600 px-4 py-2 rounded">
            Bridal Collection
          </h3>
        </div>

        {/* Everyday */}
        <div
          className="relative group cursor-pointer"
          onClick={() => navigate("/collections/everyday")}
        >
          <img
            src={everydayImg}
            alt="Everyday Wear"
            className="rounded-xl shadow-lg h-64 w-full object-cover transform group-hover:scale-105 transition duration-300"
          />
          <h3 className="absolute bottom-4 left-1/2 transform -translate-x-1/2 text-white font-semibold bg-yellow-500 px-4 py-2 rounded">
            Everyday Wear
          </h3>
        </div>

        {/* Luxury */}
        <div
          className="relative group cursor-pointer"
          onClick={() => navigate("/collections/luxury")}
        >
          <img
            src={luxuryImg}
            alt="Luxury Collection"
            className="rounded-xl shadow-lg h-64 w-full object-cover transform group-hover:scale-105 transition duration-300"
          />
          <h3 className="absolute bottom-4 left-1/2 transform -translate-x-1/2 text-white font-semibold bg-purple-600 px-4 py-2 rounded">
            Luxury Collection
          </h3>
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-gray-800 text-white text-center py-6 mt-auto">
        <p>Â© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
