import React from "react";
import { useNavigate } from "react-router-dom";
import aboutImg from "../assets/about-small.jpg"; // Use a smaller square image

const About = () => {
  const navigate = useNavigate();

  return (
    <div className="about-container">
      {/* About Section */}
      <section className="about-section">
        <div className="about-image">
          <img src={aboutImg} alt="Elegant Jewelry" />
        </div>
        <div className="about-content">
          <h1>About Elegant Jewelry</h1>
          <p>
            Welcome to <strong>Elegant Jewelry</strong>, where beauty meets craftsmanship. 
            We create timeless pieces that tell stories of love, joy, and elegance. 
            From Bridal to Everyday to Luxury collections, every jewel is crafted with precision and care.
          </p>
          <p>
            Each piece is designed to enhance your moments, whether it's your wedding day or the glow of daily life.
          </p>
          <button onClick={() => navigate("/collections")} className="collections-btn">
            Explore Collections
          </button>
        </div>
      </section>
    </div>
  );
};

export default About;
