import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

// Images
import ring1 from "../assets/bridal-ring1.jpg"; // Gold + Red Stone
import ring2 from "../assets/bridal-ring2.jpg"; // White + Green Stones
import ring3 from "../assets/bridal-ring3.jpg"; // Pearl + Red Stone
import logo from "../assets/logo.jpg";

const BridalRings = () => {
  const navigate = useNavigate();
  const [search, setSearch] = useState("");

  const rings = [
    {
      name: "Golden Elegance Ring",
      img: ring1,
      desc: "Elegant golden ring featuring a red stone centerpiece, perfect for a classy and royal bridal look.",
      price: "₹20,000",
    },
    {
      name: "Festive White & Green Ring",
      img: ring2,
      desc: "White and green stones embedded in a delicate design, giving a festive and traditional charm.",
      price: "₹18,500",
    },
    {
      name: "Diamond & Green Stone Ring",
      img: ring3,
      desc: "Timeless ring with a diamond and green stone combination, ideal for a graceful and elegant bridal appearance.",
      price: "₹22,000",
    },
  ];

  const filtered = rings.filter((item) =>
    item.name.toLowerCase().includes(search.toLowerCase())
  );

  // Add to Cart
  const handleAddToCart = (item) => {
    const cart = JSON.parse(localStorage.getItem("cartItems")) || [];
    cart.push(item);
    localStorage.setItem("cartItems", JSON.stringify(cart));
    alert(`${item.name} added to Cart!`);
  };

  // Buy Now → directly add to Orders as unplaced
  const handleBuyNow = (item) => {
    const orders = JSON.parse(localStorage.getItem("ordersPlaced")) || [];
    orders.push({ ...item, placed: false });
    localStorage.setItem("ordersPlaced", JSON.stringify(orders));
    alert(`${item.name} added to Orders!`);
    navigate("/orders");
  };

  // Add to Wishlist
  const handleWishlist = (item) => {
    const wishlist = JSON.parse(localStorage.getItem("wishlistItems")) || [];
    if (!wishlist.find(i => i.name === item.name)) {
      wishlist.push(item);
      localStorage.setItem("wishlistItems", JSON.stringify(wishlist));
      alert(`${item.name} added to Wishlist!`);
    } else {
      alert(`${item.name} is already in Wishlist!`);
    }
  };

  return (
    <div className="home-container">
      {/* Header */}
      <header style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "15px 30px", backgroundColor: "#f8f8f8" }}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <img src={logo} alt="Elegant Jewelry Logo" style={{ height: "60px", marginRight: "15px" }} />
          <span style={{ fontWeight: "bold", fontSize: "24px", color: "#d6336c" }}>Elegant Jewelry</span>
        </div>
        <ul style={{ display: "flex", listStyle: "none", gap: "25px", fontWeight: "500" }}>
          <li><a href="/" style={{ textDecoration: "none", color: "#000" }}>Home</a></li>
          <li><a href="/about" style={{ textDecoration: "none", color: "#000" }}>About</a></li>
          <li><a href="/collections" style={{ textDecoration: "none", color: "#000" }}>Collections</a></li>
          <li><a href="/orders" style={{ textDecoration: "none", color: "#000" }}>Orders</a></li>
          <li><a href="/wishlist" style={{ textDecoration: "none", color: "#000" }}>Wishlist</a></li>
          <li><a href="/contact" style={{ textDecoration: "none", color: "#000" }}>Contact</a></li>
        </ul>
      </header>

      {/* Search Bar */}
      <div style={{ textAlign: "center", margin: "40px 0 20px 0" }}>
        <input
          type="text"
          placeholder="Search Rings..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{ padding: "10px 20px", borderRadius: "25px", border: "2px solid #d6336c", width: "260px", outline: "none", fontSize: "15px" }}
        />
        <button
          style={{ marginLeft: "10px", padding: "10px 25px", borderRadius: "25px", border: "none", backgroundColor: "#d6336c", color: "white", fontWeight: "500", cursor: "pointer" }}
          onClick={() => alert(`Searching for: ${search}`)}
        >
          Search
        </button>
      </div>

      {/* Rings Section */}
      <section style={{ padding: "0 30px" }}>
        <h2 style={{ fontSize: "28px", marginBottom: "25px" }}>Bridal Rings</h2>
        <div style={{ display: "flex", flexWrap: "wrap", gap: "25px" }}>
          {filtered.map((item, index) => (
            <div key={index} style={{ border: "1px solid #ddd", borderRadius: "10px", padding: "10px", width: "220px", textAlign: "center" }}>
              <img src={item.img} alt={item.name} style={{ width: "100%", height: "180px", objectFit: "cover", borderRadius: "10px" }} />
              <h3 style={{ margin: "10px 0 5px 0" }}>{item.name}</h3>
              <p>{item.desc}</p>
              <p style={{ fontWeight: "bold", marginBottom: "10px" }}>{item.price}</p>
              <div style={{ display: "flex", gap: "10px", flexWrap: "wrap", justifyContent: "center" }}>
                <button style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 15px", borderRadius: "20px", border: "none", cursor: "pointer" }} onClick={() => handleBuyNow(item)}>Buy Now</button>
                <button style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 15px", borderRadius: "20px", border: "none", cursor: "pointer" }} onClick={() => handleAddToCart(item)}>Add to Cart</button>
                <button style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 20px", borderRadius: "20px", border: "none", cursor: "pointer" }} onClick={() => handleWishlist(item)}>Wishlist</button>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer style={{ backgroundColor: "#000", color: "#fff", padding: "25px 0", textAlign: "center", marginTop: "50px" }}>
        <p style={{ margin: 0, fontSize: "16px" }}>© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default BridalRings;
