import React, { useState, useEffect } from "react";
import logo from "../assets/logo.jpg";

const Order = () => {
  // Retrieve stored items from localStorage
  const [cartItems, setCartItems] = useState(() => JSON.parse(localStorage.getItem("cartItems")) || []);
  const [buyNowItems, setBuyNowItems] = useState(() => JSON.parse(localStorage.getItem("buyNowItems")) || []);
  const [wishlistItems, setWishlistItems] = useState(() => JSON.parse(localStorage.getItem("wishlistItems")) || []);
  const [ordersPlaced, setOrdersPlaced] = useState(() => JSON.parse(localStorage.getItem("ordersPlaced")) || []);

  // Sync state with localStorage
  useEffect(() => {
    localStorage.setItem("cartItems", JSON.stringify(cartItems));
    localStorage.setItem("buyNowItems", JSON.stringify(buyNowItems));
    localStorage.setItem("wishlistItems", JSON.stringify(wishlistItems));
    localStorage.setItem("ordersPlaced", JSON.stringify(ordersPlaced));
  }, [cartItems, buyNowItems, wishlistItems, ordersPlaced]);

  // Place order for all items
  const handlePlaceOrder = () => {
    const newOrders = [...ordersPlaced, ...cartItems, ...buyNowItems];
    setOrdersPlaced(newOrders);
    setCartItems([]);
    setBuyNowItems([]);
    alert("Order placed successfully!");
  };

  // Remove single item from cart/buyNow/wishlist
  const handleRemoveItem = (type, index) => {
    if (type === "cart") {
      const updated = [...cartItems];
      updated.splice(index, 1);
      setCartItems(updated);
    } else if (type === "buyNow") {
      const updated = [...buyNowItems];
      updated.splice(index, 1);
      setBuyNowItems(updated);
    } else if (type === "wishlist") {
      const updated = [...wishlistItems];
      updated.splice(index, 1);
      setWishlistItems(updated);
    }
  };

  return (
    <div className="order-page" style={{ fontFamily: "Arial, sans-serif" }}>
      {/* Header */}
      <header className="navbar" style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "15px 30px", backgroundColor: "#000", color: "#fff" }}>
        <div className="logo" style={{ display: "flex", alignItems: "center" }}>
          <img src={logo} alt="Elegant Jewelry Logo" style={{ width: "60px", marginRight: "15px" }} />
          <span style={{ fontSize: "20px", fontWeight: "bold" }}>Elegant Jewelry - Orders</span>
        </div>
      </header>

      <div style={{ padding: "30px" }}>
        {/* Cart Items */}
        <h2>Cart Items</h2>
        {cartItems.length === 0 ? (
          <p>No items in Cart.</p>
        ) : (
          <div style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
            {cartItems.map((item, index) => (
              <div key={index} style={{ display: "flex", alignItems: "center", gap: "15px", border: "1px solid #ccc", padding: "10px", borderRadius: "8px" }}>
                <img src={item.img} alt={item.name} style={{ width: "80px", height: "80px", objectFit: "cover", borderRadius: "5px" }} />
                <div style={{ flex: 1 }}>
                  <h4 style={{ margin: "0 0 5px 0" }}>{item.name}</h4>
                  <p style={{ margin: "0 0 5px 0", fontWeight: "bold" }}>{item.price}</p>
                </div>
                <button
                  onClick={() => handleRemoveItem("cart", index)}
                  style={{ padding: "5px 12px", backgroundColor: "#d6336c", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer" }}
                >
                  Remove
                </button>
              </div>
            ))}
          </div>
        )}

        {/* Buy Now Items */}
        <h2 style={{ marginTop: "40px" }}>Buy Now Items</h2>
        {buyNowItems.length === 0 ? (
          <p>No Buy Now items.</p>
        ) : (
          <div style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
            {buyNowItems.map((item, index) => (
              <div key={index} style={{ display: "flex", alignItems: "center", gap: "15px", border: "1px solid #ccc", padding: "10px", borderRadius: "8px" }}>
                <img src={item.img} alt={item.name} style={{ width: "80px", height: "80px", objectFit: "cover", borderRadius: "5px" }} />
                <div style={{ flex: 1 }}>
                  <h4 style={{ margin: "0 0 5px 0" }}>{item.name}</h4>
                  <p style={{ margin: "0 0 5px 0", fontWeight: "bold" }}>{item.price}</p>
                </div>
                <button
                  onClick={() => handleRemoveItem("buyNow", index)}
                  style={{ padding: "5px 12px", backgroundColor: "#d6336c", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer" }}
                >
                  Remove
                </button>
              </div>
            ))}
          </div>
        )}

        {/* Place Order Button */}
        {(cartItems.length > 0 || buyNowItems.length > 0) && (
          <div style={{ marginTop: "30px" }}>
            <button
              onClick={handlePlaceOrder}
              style={{ padding: "10px 25px", backgroundColor: "#28a745", color: "#fff", fontWeight: "bold", border: "none", borderRadius: "8px", cursor: "pointer" }}
            >
              Place Order
            </button>
          </div>
        )}

        {/* Orders Placed */}
        <h2 style={{ marginTop: "50px" }}>Orders Placed</h2>
        {ordersPlaced.length === 0 ? (
          <p>No orders placed yet.</p>
        ) : (
          <div style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
            {ordersPlaced.map((item, index) => (
              <div key={index} style={{ display: "flex", alignItems: "center", gap: "15px", border: "1px solid #ccc", padding: "10px", borderRadius: "8px" }}>
                <img src={item.img} alt={item.name} style={{ width: "80px", height: "80px", objectFit: "cover", borderRadius: "5px" }} />
                <div style={{ flex: 1 }}>
                  <h4 style={{ margin: "0 0 5px 0" }}>{item.name}</h4>
                  <p style={{ margin: "0 0 5px 0", fontWeight: "bold" }}>{item.price}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Footer */}
      <footer style={{ backgroundColor: "#000", color: "#fff", textAlign: "center", padding: "20px 0", marginTop: "50px" }}>
        <p>Â© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default Order;
