import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.jpg";

const Orders = () => {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [buyNowItems, setBuyNowItems] = useState([]);
  const [wishlistItems, setWishlistItems] = useState([]);

  // Load items from localStorage
  useEffect(() => {
    setCartItems(JSON.parse(localStorage.getItem("cart")) || []);
    setBuyNowItems(JSON.parse(localStorage.getItem("buyNow")) || []);
    setWishlistItems(JSON.parse(localStorage.getItem("wishlist")) || []);
  }, []);

  const removeItem = (id, type) => {
    if (type === "cart") {
      const updated = cartItems.filter(item => item.id !== id);
      setCartItems(updated);
      localStorage.setItem("cart", JSON.stringify(updated));
    } else if (type === "buyNow") {
      const updated = buyNowItems.filter(item => item.id !== id);
      setBuyNowItems(updated);
      localStorage.setItem("buyNow", JSON.stringify(updated));
    } else if (type === "wishlist") {
      const updated = wishlistItems.filter(item => item.id !== id);
      setWishlistItems(updated);
      localStorage.setItem("wishlist", JSON.stringify(updated));
    }
  };

  const placeOrder = () => {
    if (cartItems.length === 0 && buyNowItems.length === 0) {
      alert("No items to place order!");
      return;
    }

    // You can implement actual order placement logic here
    alert("Order placed successfully!");

    // Clear Buy Now & Cart after placing order
    setBuyNowItems([]);
    setCartItems([]);
    localStorage.removeItem("cart");
    localStorage.removeItem("buyNow");
  };

  return (
    <div className="orders-page">
      {/* Header */}
      <header className="navbar">
        <div className="logo">
          <img src={logo} alt="Elegant Jewelry Logo" className="logo-img" />
          <span className="brand-name">Elegant Jewelry</span>
        </div>
        <ul className="nav-links">
          <li><a href="/">Home</a></li>
          <li><a href="/collections">Collections</a></li>
          <li><a href="/orders" className="active">Orders</a></li>
          <li><a href="/wishlist">Wishlist</a></li>
        </ul>
      </header>

      <div className="orders-container" style={{ padding: "20px" }}>
        {/* Cart Items */}
        <section>
          <h2>Cart Items</h2>
          {cartItems.length === 0 ? (
            <p>No items in Cart.</p>
          ) : (
            cartItems.map(item => (
              <div key={item.id} style={{ display: "flex", gap: "20px", marginBottom: "10px", alignItems: "center" }}>
                <img src={item.img} alt={item.name} style={{ width: "80px" }} />
                <div>
                  <p>{item.name}</p>
                  <p>{item.price}</p>
                </div>
                <button onClick={() => removeItem(item.id, "cart")} style={{ cursor: "pointer" }}>Remove</button>
              </div>
            ))
          )}
        </section>

        {/* Buy Now Items */}
        <section>
          <h2>Buy Now Items</h2>
          {buyNowItems.length === 0 ? (
            <p>No Buy Now items.</p>
          ) : (
            buyNowItems.map(item => (
              <div key={item.id} style={{ display: "flex", gap: "20px", marginBottom: "10px", alignItems: "center" }}>
                <img src={item.img} alt={item.name} style={{ width: "80px" }} />
                <div>
                  <p>{item.name}</p>
                  <p>{item.price}</p>
                </div>
                <button onClick={() => removeItem(item.id, "buyNow")} style={{ cursor: "pointer" }}>Remove</button>
              </div>
            ))
          )}
        </section>

        {/* Wishlist Items */}
        <section>
          <h2>Wishlist Items</h2>
          {wishlistItems.length === 0 ? (
            <p>No items in Wishlist.</p>
          ) : (
            wishlistItems.map(item => (
              <div key={item.id} style={{ display: "flex", gap: "20px", marginBottom: "10px", alignItems: "center" }}>
                <img src={item.img} alt={item.name} style={{ width: "80px" }} />
                <div>
                  <p>{item.name}</p>
                  <p>{item.price}</p>
                </div>
                <button onClick={() => removeItem(item.id, "wishlist")} style={{ cursor: "pointer" }}>Remove</button>
              </div>
            ))
          )}
        </section>

        {/* Place Order Button */}
        <div style={{ marginTop: "20px" }}>
          <button onClick={placeOrder} style={{ padding: "10px 20px", backgroundColor: "#d6336c", color: "#fff", border: "none", borderRadius: "10px", cursor: "pointer" }}>
            Place Order
          </button>
        </div>
      </div>
    </div>
  );
};

export default Orders;
