import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

// Images
import logo from "../assets/logo.jpg";
import necklace from "../assets/everyday-necklace.jpg";
import earrings from "../assets/everyday-earrings.jpg";
import ring from "../assets/everyday-ring.jpg";
import bracelet from "../assets/everyday-bracelet.jpg";

const EverydayCollection = () => {
  const navigate = useNavigate();
  const [search, setSearch] = useState("");

  const items = [
    {
      name: "Simple Gold Necklace",
      img: necklace,
      desc: "Elegant and lightweight gold necklace, perfect for daily wear with any outfit.",
      price: "₹7,500",
    },
    {
      name: "Minimal Pearl Earrings",
      img: earrings,
      desc: "Delicate pearl earrings designed for comfort and simplicity, ideal for everyday use.",
      price: "₹3,500",
    },
    {
      name: "Classic Everyday Ring",
      img: ring,
      desc: "Simple and elegant ring suitable for daily wear, adding a subtle charm to your style.",
      price: "₹5,000",
    },
    {
      name: "Lightweight Everyday Bracelet",
      img: bracelet,
      desc: "Comfortable and stylish bracelet designed for everyday use, perfect for casual outfits.",
      price: "₹6,000",
    },
  ];

  const filtered = items.filter((item) =>
    item.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="home-container">
      {/* Header */}
      <header className="navbar">
        <div className="logo">
          <img src={logo} alt="Elegant Jewelry Logo" className="logo-img" />
          <span className="brand-name">Elegant Jewelry</span>
        </div>
        <ul className="nav-links">
          <li><a href="/">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/collections">Collections</a></li>
          <li><a href="/orders">Orders</a></li>
          <li><a href="/wishlist">Wishlist</a></li>
          <li><a href="/contact">Contact</a></li>
        </ul>
      </header>

      {/* Search Bar */}
      <div className="search-bar" style={{ textAlign: "center", margin: "40px 0 20px 0" }}>
        <input
          type="text"
          placeholder="Search Everyday Jewelry..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{
            padding: "10px 20px",
            borderRadius: "25px",
            border: "2px solid #d6336c",
            width: "260px",
            outline: "none",
            fontSize: "15px",
          }}
        />
        <button
          style={{
            marginLeft: "10px",
            padding: "10px 25px",
            borderRadius: "25px",
            border: "none",
            backgroundColor: "#d6336c",
            color: "white",
            fontWeight: "500",
            cursor: "pointer",
          }}
          onClick={() => alert(`Searching for: ${search}`)}
        >
          Search
        </button>
      </div>

      {/* Everyday Collection Section */}
      <section className="collections-section">
        <h2 className="collections-title">Everyday Collection</h2>
        <div className="collections-grid">
          {filtered.map((item, index) => (
            <div key={index} className="collection-card">
              <img src={item.img} alt={item.name} />
              <div className="card-panel">
                <h3 className="collection-name">{item.name}</h3>
                <p className="collection-desc">{item.desc}</p>
                <p className="collection-price" style={{ fontWeight: "bold", marginBottom: "10px" }}>
                  {item.price}
                </p>

                {/* Buttons Row */}
                <div className="button-row" style={{ display: "flex", gap: "10px", marginBottom: "10px" }}>
                  <button
                    className="buy-btn"
                    onClick={() => alert(`Buying: ${item.name}`)}
                    style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 15px", borderRadius: "20px", border: "none", cursor: "pointer" }}
                  >
                    Buy Now
                  </button>
                  <button
                    className="addcart-btn"
                    onClick={() => alert(`Added to Cart: ${item.name}`)}
                    style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 15px", borderRadius: "20px", border: "none", cursor: "pointer" }}
                  >
                    Add to Cart
                  </button>
                </div>

                <div className="wishlist-row">
                  <button
                    className="wishlist-btn"
                    onClick={() => alert(`Added to Wishlist: ${item.name}`)}
                    style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 20px", borderRadius: "20px", border: "none", cursor: "pointer" }}
                  >
                    Wishlist
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer className="site-footer" style={{ backgroundColor: "#000", color: "#fff", padding: "20px 0", textAlign: "center", marginTop: "50px" }}>
        <p>© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default EverydayCollection;
