import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Home from "./pages/Home";
import About from "./pages/About";
import Collections from "./pages/Collection";
import BridalCollection from "./pages/BridalCollection";
import EverydayCollection from "./pages/EverydayCollection";
import LuxuryCollection from "./pages/LuxuryCollection";
import Orders from "./pages/Orders";
import Wishlist from "./pages/Wishlist";
import Contact from "./pages/Contact";
import Login from "./pages/Login";
import Register from "./pages/Register";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const loggedIn = localStorage.getItem("isLoggedIn");
    setIsLoggedIn(loggedIn === "true");
  }, []);

  return (
    <Router>
      <Routes>
        {!isLoggedIn ? (
          <>
            {/* ye tab chalega jab login nahi hua */}
            <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
            <Route path="/register" element={<Register />} />
            <Route path="*" element={<Navigate to="/login" />} />
          </>
        ) : (
          <>
            {/* ye tab chalega jab login ho gaya */}
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/collections" element={<Collections />} />
            <Route path="/collections/bridal" element={<BridalCollection />} />
            <Route path="/collections/everyday" element={<EverydayCollection />} />
            <Route path="/collections/luxury" element={<LuxuryCollection />} />
            <Route path="/orders" element={<Orders />} />
            <Route path="/wishlist" element={<Wishlist />} />
            <Route path="/contact" element={<Contact />} />
            {/* agar logged in hone ke baad login/register jaye to redirect */}
            <Route path="/login" element={<Navigate to="/" />} />
            <Route path="/register" element={<Navigate to="/" />} />
            <Route path="*" element={<Navigate to="/" />} />
          </>
        )}
      </Routes>
    </Router>
  );
}

export default App;
