import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();

    // Get saved users from localStorage
    const users = JSON.parse(localStorage.getItem("users")) || [];
    const existingUser = users.find(
      (user) => user.email === email && user.password === password
    );

    if (existingUser) {
      alert("‚ú® Login successful! Welcome back üíé");
      navigate("/");
    } else {
      alert("‚ùå Invalid email or password. Please try again.");
    }
  };

  return (
    <div
      className="min-h-screen flex items-center justify-center bg-gradient-to-br from-pink-50 to-rose-100"
      style={{
        fontFamily: "Poppins, sans-serif",
      }}
    >
      <div className="bg-white shadow-lg rounded-2xl w-96 p-8 border border-rose-100">
        <h2 className="text-3xl font-bold text-center text-rose-600 mb-2">
          Elegant Jewelry
        </h2>
        <p className="text-center text-gray-500 mb-6">
          Welcome back! Please login to continue üíç
        </p>

        <form onSubmit={handleLogin}>
          <label className="block text-gray-700 text-sm mb-1">Email</label>
          <input
            type="email"
            placeholder="Enter your email"
            className="w-full mb-4 px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-rose-400"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            autoComplete="off"
          />

          <label className="block text-gray-700 text-sm mb-1">Password</label>
          <input
            type="password"
            placeholder="Enter your password"
            className="w-full mb-6 px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-rose-400"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            autoComplete="new-password"
          />

          <button
            type="submit"
            className="w-full bg-rose-500 text-white py-2 rounded-lg font-semibold hover:bg-rose-600 transition"
          >
            Login
          </button>
        </form>

        <p className="text-center text-sm text-gray-600 mt-4">
          Don‚Äôt have an account?{" "}
          <span
            onClick={() => navigate("/register")}
            className="text-rose-500 hover:underline cursor-pointer font-medium"
          >
            Register here
          </span>
        </p>
      </div>
    </div>
  );
};

export default Login;
