import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.jpg";

const Wishlist = () => {
  const navigate = useNavigate();
  const [wishlistItems, setWishlistItems] = useState(() =>
    JSON.parse(localStorage.getItem("wishlistItems")) || []
  );
  const [ordersPlaced, setOrdersPlaced] = useState(() =>
    JSON.parse(localStorage.getItem("ordersPlaced")) || []
  );

  // âœ… Sync localStorage when wishlist or orders change
  useEffect(() => {
    localStorage.setItem("wishlistItems", JSON.stringify(wishlistItems));
    localStorage.setItem("ordersPlaced", JSON.stringify(ordersPlaced));
  }, [wishlistItems, ordersPlaced]);

  // âœ… Add item to Cart
  const handleAddToCart = (item) => {
    const cart = JSON.parse(localStorage.getItem("cartItems")) || [];
    const alreadyInCart = cart.some((i) => i.name === item.name);
    if (!alreadyInCart) {
      cart.push(item);
      localStorage.setItem("cartItems", JSON.stringify(cart));
      alert(`${item.name} added to Cart ðŸ›’`);
    } else {
      alert(`${item.name} is already in your Cart.`);
    }
  };

  // âœ… Shop Now â†’ Move directly to Orders (Unplaced)
  const handleShopNow = (item) => {
    const currentOrders = JSON.parse(localStorage.getItem("ordersPlaced")) || [];
    const updatedOrders = [...currentOrders, { ...item, placed: false }];
    localStorage.setItem("ordersPlaced", JSON.stringify(updatedOrders));
    setOrdersPlaced(updatedOrders);

    // Remove from Wishlist
    const updatedWishlist = wishlistItems.filter((i) => i.name !== item.name);
    setWishlistItems(updatedWishlist);

    alert(`${item.name} moved to Orders âœ…`);
    navigate("/orders");
  };

  // âœ… Remove from Wishlist
  const handleRemove = (index) => {
    const updated = [...wishlistItems];
    updated.splice(index, 1);
    setWishlistItems(updated);
  };

  // ðŸ’… Styles
  const cardStyle = {
    border: "1px solid #ddd",
    borderRadius: "12px",
    padding: "12px",
    width: "230px",
    textAlign: "center",
    boxShadow: "0 3px 8px rgba(0,0,0,0.1)",
    backgroundColor: "#fff",
  };

  const imgStyle = {
    width: "100%",
    height: "180px",
    objectFit: "cover",
    borderRadius: "10px",
  };

  const btnStyle = {
    backgroundColor: "#d6336c",
    color: "#fff",
    padding: "6px 12px",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    margin: "6px 3px",
    fontSize: "14px",
    transition: "0.3s",
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        minHeight: "100vh",
        backgroundColor: "#f9f9f9",
      }}
    >
      {/* âœ… Header same as Home */}
      <header
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          padding: "15px 30px",
          backgroundColor: "#fff",
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
        }}
      >
        <div style={{ display: "flex", alignItems: "center" }}>
          <img
            src={logo}
            alt="Elegant Jewelry Logo"
            style={{ height: "60px", marginRight: "15px" }}
          />
          <span
            style={{
              fontWeight: "bold",
              fontSize: "24px",
              color: "#d6336c",
              letterSpacing: "0.5px",
            }}
          >
            Elegant Jewelry
          </span>
        </div>
        <ul
          style={{
            display: "flex",
            listStyle: "none",
            gap: "25px",
            fontWeight: "500",
          }}
        >
          <li>
            <a href="/" style={{ textDecoration: "none", color: "#000" }}>
              Home
            </a>
          </li>
          <li>
            <a href="/about" style={{ textDecoration: "none", color: "#000" }}>
              About
            </a>
          </li>
          <li>
            <a
              href="/collections"
              style={{ textDecoration: "none", color: "#000" }}
            >
              Collections
            </a>
          </li>
          <li>
            <a href="/orders" style={{ textDecoration: "none", color: "#000" }}>
              Orders
            </a>
          </li>
          <li>
            <a
              href="/wishlist"
              className="active"
              style={{
                textDecoration: "none",
                color: "#d6336c",
                fontWeight: "bold",
              }}
            >
              Wishlist
            </a>
          </li>
          <li>
            <a
              href="/contact"
              style={{ textDecoration: "none", color: "#000" }}
            >
              Contact
            </a>
          </li>
        </ul>
      </header>

      {/* âœ… Wishlist Section */}
      <div style={{ flex: 1, padding: "40px 30px" }}>
        <h2
          style={{
            fontSize: "28px",
            marginBottom: "25px",
            color: "#333",
            fontWeight: "600",
          }}
        >
          Wishlist Items
        </h2>

        {wishlistItems.length === 0 ? (
          <p style={{ fontSize: "16px" }}>No items in Wishlist.</p>
        ) : (
          <div
            style={{
              display: "flex",
              flexWrap: "wrap",
              gap: "20px",
              justifyContent: "flex-start",
            }}
          >
            {wishlistItems.map((item, index) => (
              <div key={index} style={cardStyle}>
                <img src={item.img} alt={item.name} style={imgStyle} />
                <h4 style={{ margin: "10px 0 5px 0", color: "#222" }}>
                  {item.name}
                </h4>
                <p
                  style={{
                    fontWeight: "bold",
                    marginBottom: "10px",
                    color: "#555",
                  }}
                >
                  â‚¹{item.price}
                </p>
                <div>
                  <button
                    style={btnStyle}
                    onClick={() => handleShopNow(item)}
                  >
                    Shop Now
                  </button>
                  <button
                    style={{ ...btnStyle, backgroundColor: "#6c757d" }}
                    onClick={() => handleAddToCart(item)}
                  >
                    Add to Cart
                  </button>
                  <button
                    style={{ ...btnStyle, backgroundColor: "#ff4d4d" }}
                    onClick={() => handleRemove(index)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* âœ… Footer */}
      <footer
        style={{
          backgroundColor: "#000",
          color: "#fff",
          textAlign: "center",
          padding: "25px 0",
          marginTop: "auto",
        }}
      >
        <p style={{ margin: 0, fontSize: "16px" }}>
          Â© 2025 Elegant Jewelry. All Rights Reserved.
        </p>
      </footer>
    </div>
  );
};

export default Wishlist;
