import React, { useState, useEffect } from "react";
import logo from "../assets/logo.jpg";

const Orders = () => {
  const [cartItems, setCartItems] = useState([]);
  const [orders, setOrders] = useState([]);

  // Load items from localStorage
  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem("cartItems")) || [];
    const savedOrders = JSON.parse(localStorage.getItem("orders")) || [];
    setCartItems(savedCart);
    setOrders(savedOrders);
  }, []);

  // Save cart & orders whenever updated
  useEffect(() => {
    localStorage.setItem("cartItems", JSON.stringify(cartItems));
  }, [cartItems]);

  useEffect(() => {
    localStorage.setItem("orders", JSON.stringify(orders));
  }, [orders]);

  // Buy Now from cart
  const handleBuyNow = (item) => {
    setOrders([...orders, item]);
    setCartItems(cartItems.filter((i) => i.id !== item.id));
    alert(`‚úÖ Order Placed: ${item.name}`);
  };

  // Remove from cart
  const handleRemoveCart = (id) => {
    setCartItems(cartItems.filter((i) => i.id !== id));
    alert("‚ùå Item removed from cart");
  };

  // Remove from orders
  const handleRemoveOrder = (id) => {
    setOrders(orders.filter((i) => i.id !== id));
    alert("‚ùå Order removed");
  };

  return (
    <div className="home-container">
      {/* Navbar */}
      <header className="navbar">
        <div className="logo">
          <img src={logo} alt="Elegant Jewelry Logo" className="logo-img" />
          <span className="brand-name">Elegant Jewelry</span>
        </div>
        <ul className="nav-links">
          <li><a href="/">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/collections">Collections</a></li>
          <li><a href="/orders" className="active">Orders</a></li>
          <li><a href="/wishlist">Wishlist</a></li>
          <li><a href="/contact">Contact</a></li>
        </ul>
      </header>

      <div style={{ padding: "20px" }}>
        <h1 style={{ textAlign: "center", marginBottom: "20px" }}>üõçÔ∏è Your Cart & Orders</h1>

        {/* Cart Items */}
        {cartItems.length > 0 ? (
          <section>
            <h2>üõí Cart Items</h2>
            <div style={{ display: "flex", flexWrap: "wrap", gap: "20px", marginTop: "10px" }}>
              {cartItems.map((item) => (
                <div key={item.id} style={{
                  border: "1px solid #ddd",
                  borderRadius: "12px",
                  padding: "10px",
                  width: "250px",
                  textAlign: "center",
                  boxShadow: "0 2px 5px rgba(0,0,0,0.1)"
                }}>
                  <img src={item.img} alt={item.name} style={{ width: "100%", height: "220px", objectFit: "cover", borderRadius: "10px" }} />
                  <h3>{item.name}</h3>
                  <p><b>{item.collection}</b></p>
                  <p>{item.price}</p>
                  <div style={{ display: "flex", justifyContent: "center", gap: "10px" }}>
                    <button
                      onClick={() => handleBuyNow(item)}
                      style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 15px", border: "none", borderRadius: "20px", cursor: "pointer" }}
                    >
                      Buy Now
                    </button>
                    <button
                      onClick={() => handleRemoveCart(item.id)}
                      style={{ backgroundColor: "#888", color: "#fff", padding: "8px 15px", border: "none", borderRadius: "20px", cursor: "pointer" }}
                    >
                      Remove
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </section>
        ) : (
          <p style={{ textAlign: "center", color: "gray", marginTop: "40px" }}>üõí Your cart is empty.</p>
        )}

        {/* Orders Placed */}
        {orders.length > 0 && (
          <section style={{ marginTop: "50px" }}>
            <h2>‚úÖ Orders Placed</h2>
            <div style={{ display: "flex", flexWrap: "wrap", gap: "20px", marginTop: "10px" }}>
              {orders.map((item) => (
                <div key={item.id} style={{
                  border: "1px solid #ddd",
                  borderRadius: "12px",
                  padding: "10px",
                  width: "250px",
                  textAlign: "center",
                  backgroundColor: "#f8f8f8"
                }}>
                  <img src={item.img} alt={item.name} style={{ width: "100%", height: "220px", objectFit: "cover", borderRadius: "10px" }} />
                  <h3>{item.name}</h3>
                  <p><b>{item.collection}</b></p>
                  <p>{item.price}</p>
                  <p style={{ color: "green", fontWeight: "bold" }}>Order Placed ‚úÖ</p>
                  <button
                    onClick={() => handleRemoveOrder(item.id)}
                    style={{ backgroundColor: "#888", color: "#fff", padding: "8px 15px", border: "none", borderRadius: "20px", cursor: "pointer" }}
                  >
                    Remove
                  </button>
                </div>
              ))}
            </div>
          </section>
        )}
      </div>

      <footer style={{ backgroundColor: "#000", color: "#fff", textAlign: "center", padding: "20px", marginTop: "50px" }}>
        ¬© 2025 Elegant Jewelry. All Rights Reserved.
      </footer>
    </div>
  );
};

export default Orders;
