import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios"; // For backend API calls

// Images
import logo from "../assets/logo.jpg";
import ring1 from "../assets/luxury-ring1.jpg"; // Silver with one diamond
import ring2 from "../assets/luxury-ring2.jpg"; // Silver with multiple silver & white stones
import ring3 from "../assets/luxury-ring3.jpg"; // Golden with white diamonds + white nag

const LuxuryRings = () => {
  const navigate = useNavigate();
  const [search, setSearch] = useState("");

  const rings = [
    { 
      name: "Silver Solitaire Diamond Ring", 
      img: ring1,
      desc: "Elegant silver ring featuring a single sparkling diamond at the center.",
      price: "₹60,000"
    },
    { 
      name: "Silver Multi-Stone Ring", 
      img: ring2,
      desc: "Sophisticated silver ring adorned with multiple silver and white stones for a shimmering look.",
      price: "₹75,000"
    },
    { 
      name: "Golden Diamond & White Stone Ring", 
      img: ring3,
      desc: "Luxury golden ring featuring white diamonds and a striking white nag centerpiece.",
      price: "₹1,20,000"
    },
  ];

  const filtered = rings.filter((item) =>
    item.name.toLowerCase().includes(search.toLowerCase())
  );

  // Add to Cart → Backend
  const handleAddToCart = async (item) => {
    try {
      await axios.post("http://localhost:5000/cart", item);
      alert(`${item.name} added to Cart!`);
    } catch (err) {
      console.error(err);
      alert("Failed to add to Cart!");
    }
  };

  // Buy Now → Backend Orders
  const handleBuyNow = async (item) => {
    try {
      await axios.post("http://localhost:5000/orders", item);
      alert(`${item.name} added to Orders!`);
      navigate("/orders");
    } catch (err) {
      console.error(err);
      alert("Failed to place order!");
    }
  };

  // Wishlist → Backend
  const handleWishlist = async (item) => {
    try {
      const res = await axios.post("http://localhost:5000/wishlist", item);
      alert(res.data.message);
    } catch (err) {
      console.error(err);
      alert("Failed to add to Wishlist!");
    }
  };

  return (
    <div className="home-container">
      {/* Header */}
      <header className="navbar">
        <div className="logo">
          <img src={logo} alt="Elegant Jewelry Logo" className="logo-img" />
          <span className="brand-name">Elegant Jewelry</span>
        </div>
        <ul className="nav-links">
          <li><a href="/">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/collections">Collections</a></li>
          <li><a href="/orders">Orders</a></li>
          <li><a href="/wishlist">Wishlist</a></li>
          <li><a href="/contact">Contact</a></li>
        </ul>
      </header>

      {/* Search Bar */}
      <div className="search-bar" style={{ textAlign: "center", margin: "40px 0 20px 0" }}>
        <input
          type="text"
          placeholder="Search Rings..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{
            padding: "10px 20px",
            borderRadius: "25px",
            border: "2px solid #d6336c",
            width: "260px",
            outline: "none",
            fontSize: "15px",
          }}
        />
        <button
          style={{
            marginLeft: "10px",
            padding: "10px 25px",
            borderRadius: "25px",
            border: "none",
            backgroundColor: "#d6336c",
            color: "white",
            fontWeight: "500",
            cursor: "pointer",
          }}
          onClick={() => alert(`Searching for: ${search}`)}
        >
          Search
        </button>
      </div>

      {/* Rings Section */}
      <section className="collections-section">
        <h2 className="collections-title">Luxury Rings</h2>
        <div className="collections-grid">
          {filtered.map((item, index) => (
            <div key={index} className="collection-card">
              <img src={item.img} alt={item.name} />
              <div className="card-panel">
                <h3 className="collection-name">{item.name}</h3>
                <p className="collection-desc">{item.desc}</p>
                <p className="collection-price" style={{ fontWeight: "bold", marginBottom: "10px" }}>
                  {item.price}
                </p>

                {/* Buttons Row */}
                <div className="button-row" style={{ display: "flex", gap: "10px", marginBottom: "10px" }}>
                  <button 
                    className="buy-btn"
                    onClick={() => handleBuyNow(item)}
                    style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 15px", borderRadius: "20px", border: "none", cursor: "pointer" }}
                  >
                    Buy Now
                  </button>
                  <button 
                    className="addcart-btn"
                    onClick={() => handleAddToCart(item)}
                    style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 15px", borderRadius: "20px", border: "none", cursor: "pointer" }}
                  >
                    Add to Cart
                  </button>
                </div>
                <div className="wishlist-row">
                  <button 
                    className="wishlist-btn"
                    onClick={() => handleWishlist(item)}
                    style={{ backgroundColor: "#d6336c", color: "#fff", padding: "8px 20px", borderRadius: "20px", border: "none", cursor: "pointer" }}
                  >
                    Wishlist
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer className="site-footer" style={{ backgroundColor: "#000", color: "#fff", padding: "20px 0", textAlign: "center", marginTop: "50px" }}>
        <p>© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default LuxuryRings;
