import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.jpg";

const Wishlist = () => {
  const navigate = useNavigate();
  const [wishlistItems, setWishlistItems] = useState(() => JSON.parse(localStorage.getItem("wishlistItems")) || []);
  const [ordersPlaced, setOrdersPlaced] = useState(() => JSON.parse(localStorage.getItem("ordersPlaced")) || []);
  const [cartItems, setCartItems] = useState(() => JSON.parse(localStorage.getItem("cartItems")) || []);

  // Sync localStorage on changes
  useEffect(() => {
    localStorage.setItem("wishlistItems", JSON.stringify(wishlistItems));
    localStorage.setItem("ordersPlaced", JSON.stringify(ordersPlaced));
    localStorage.setItem("cartItems", JSON.stringify(cartItems));
  }, [wishlistItems, ordersPlaced, cartItems]);

  // Add to Cart
  const handleAddToCart = (item) => {
    const updatedCart = [...cartItems, item];
    setCartItems(updatedCart);
    alert(`${item.name} added to Cart!`);
  };

  // Buy Now → add directly to Orders as unplaced
  const handleBuyNow = (item) => {
    const updatedOrders = [...ordersPlaced, { ...item, placed: false }];
    setOrdersPlaced(updatedOrders);

    // Remove from wishlist after buying
    const updatedWishlist = wishlistItems.filter(i => i.name !== item.name);
    setWishlistItems(updatedWishlist);

    alert(`${item.name} moved to Orders!`);
    navigate("/orders");
  };

  // Remove from Wishlist
  const handleRemove = (index) => {
    const updated = [...wishlistItems];
    updated.splice(index, 1);
    setWishlistItems(updated);
  };

  const cardStyle = {
    border: "1px solid #ddd",
    borderRadius: "10px",
    padding: "10px",
    width: "220px",
    textAlign: "center",
  };

  const imgStyle = {
    width: "100%",
    height: "180px",
    objectFit: "cover",
    borderRadius: "10px",
  };

  const btnStyle = {
    backgroundColor: "#d6336c",
    color: "#fff",
    padding: "5px 10px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    margin: "5px 3px",
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", minHeight: "100vh" }}>
      {/* Header */}
      <header style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "10px 20px", backgroundColor: "#f8f8f8" }}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <img src={logo} alt="Elegant Jewelry Logo" style={{ height: "50px", marginRight: "10px" }} />
          <span style={{ fontWeight: "bold", fontSize: "20px" }}>Elegant Jewelry</span>
        </div>
        <ul style={{ display: "flex", listStyle: "none", gap: "15px" }}>
          <li><a href="/">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/collections">Collections</a></li>
          <li><a href="/orders">Orders</a></li>
          <li><a href="/wishlist" className="active">Wishlist</a></li>
          <li><a href="/contact">Contact</a></li>
        </ul>
      </header>

      {/* Wishlist Content */}
      <div style={{ flex: 1, padding: "30px" }}>
        <h2>Wishlist Items</h2>
        {wishlistItems.length === 0 ? (
          <p>No items in Wishlist.</p>
        ) : (
          <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
            {wishlistItems.map((item, index) => (
              <div key={index} style={cardStyle}>
                <img src={item.img} alt={item.name} style={imgStyle} />
                <h4>{item.name}</h4>
                <p style={{ fontWeight: "bold" }}>{item.price}</p>
                <div>
                  <button style={btnStyle} onClick={() => handleBuyNow(item)}>Buy Now</button>
                  <button style={btnStyle} onClick={() => handleAddToCart(item)}>Add to Cart</button>
                  <button style={btnStyle} onClick={() => handleRemove(index)}>Remove</button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Footer */}
      <footer style={{ backgroundColor: "#000", color: "#fff", textAlign: "center", padding: "20px 0", marginTop: "auto" }}>
        <p>© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default Wishlist;
