import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import logo from "../assets/logo.jpg";
import ring1 from "../assets/luxury-ring1.jpg";
import ring2 from "../assets/luxury-ring2.jpg";
import ring3 from "../assets/luxury-ring3.jpg";

const LuxuryRings = () => {
  const navigate = useNavigate();
  const [search, setSearch] = useState("");

  const rings = [
    { name: "Silver Solitaire Diamond Ring", img: ring1, desc: "Elegant silver ring featuring a single sparkling diamond at the center.", price: "₹60,000" },
    { name: "Silver Multi-Stone Ring", img: ring2, desc: "Sophisticated silver ring adorned with multiple silver and white stones for a shimmering look.", price: "₹75,000" },
    { name: "Golden Diamond & White Stone Ring", img: ring3, desc: "Luxury golden ring featuring white diamonds and a striking white nag centerpiece.", price: "₹1,20,000" },
  ];

  const filtered = rings.filter(r => r.name.toLowerCase().includes(search.toLowerCase()));

  const handleAddToCart = async (item) => {
    await axios.post("http://localhost:5000/cart", item);
    alert(`${item.name} added to cart!`);
  };

  const handleBuyNow = async (item) => {
    await axios.post("http://localhost:5000/orders", item);
    alert(`${item.name} ordered!`);
    navigate("/orders");
  };

  const handleWishlist = async (item) => {
    const res = await axios.post("http://localhost:5000/wishlist", item);
    alert(res.data.message);
  };

  return (
    <div>
      <header>
        <img src={logo} alt="Logo" />
      </header>
      <input type="text" placeholder="Search Rings..." value={search} onChange={(e) => setSearch(e.target.value)} />
      <div>
        {filtered.map((item, i) => (
          <div key={i}>
            <img src={item.img} alt={item.name} />
            <h3>{item.name}</h3>
            <p>{item.desc}</p>
            <p>{item.price}</p>
            <button onClick={() => handleBuyNow(item)}>Buy Now</button>
            <button onClick={() => handleAddToCart(item)}>Add to Cart</button>
            <button onClick={() => handleWishlist(item)}>Wishlist</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default LuxuryRings;
