import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.jpg";
import bridalImg from "../assets/bridal1.jpg";
import everydayImg from "../assets/everyday1.jpg";
import luxuryImg from "../assets/luxury1.jpg";

const Collections = () => {
  const navigate = useNavigate();
  const [search, setSearch] = useState("");

  const collections = [
    {
      name: "Bridal Collection",
      img: bridalImg,
      desc: "Stunning jewelry for your special day — earrings, necklaces, rings, bracelets.",
      route: "/collections/bridal",
    },
    {
      name: "Everyday Collection",
      img: everydayImg,
      desc: "Stylish daily wear — necklaces, earrings, rings, bracelets.",
      route: "/collections/everyday",
    },
    {
      name: "Luxury Collection",
      img: luxuryImg,
      desc: "Exclusive jewelry for special occasions — necklaces, earrings, rings, bracelets.",
      route: "/collections/luxury",
    },
  ];

  const filteredCollections = collections.filter((c) =>
    c.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ fontFamily: "Arial, sans-serif" }}>
      {/* Navbar */}
      <header style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "10px 20px", backgroundColor: "#fff", borderBottom: "1px solid #ddd" }}>
        <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
          <img src={logo} alt="Elegant Jewelry Logo" style={{ width: "60px" }} />
          <span style={{ fontSize: "20px", fontWeight: "600" }}>Elegant Jewelry</span>
        </div>
        <ul style={{ display: "flex", listStyle: "none", gap: "15px" }}>
          <li><a href="/">Home</a></li>
          <li><a href="/about">About</a></li>
          <li><a href="/collections" style={{ fontWeight: "bold", color: "#d6336c" }}>Collections</a></li>
          <li><a href="/orders">Orders</a></li>
          <li><a href="/wishlist">Wishlist</a></li>
          <li><a href="/contact">Contact</a></li>
        </ul>
      </header>

      {/* Hero / Search */}
      <div style={{ textAlign: "center", padding: "30px 20px" }}>
        <h1 style={{ marginBottom: "10px" }}>Our Collections</h1>
        <p style={{ color: "#555", marginBottom: "20px" }}>
          Discover our curated collections — Bridal, Everyday, and Luxury — each crafted to reflect elegance, style, and timeless beauty.
        </p>
        <input
          type="text"
          placeholder="Search Collections..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{
            padding: "10px 20px",
            borderRadius: "25px",
            border: "2px solid #d6336c",
            width: "250px",
            outline: "none",
            fontSize: "15px",
          }}
        />
      </div>

      {/* Collections Grid */}
      <div style={{
        display: "grid",
        gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
        gap: "25px",
        padding: "0 20px 40px 20px"
      }}>
        {filteredCollections.map((c) => (
          <div key={c.name} style={{ border: "1px solid #ddd", borderRadius: "10px", overflow: "hidden", transition: "transform 0.2s", cursor: "pointer" }} onClick={() => navigate(c.route)}>
            <img src={c.img} alt={c.name} style={{ width: "100%", height: "220px", objectFit: "cover" }} />
            <div style={{ padding: "15px", textAlign: "center" }}>
              <h3 style={{ marginBottom: "10px" }}>{c.name}</h3>
              <p style={{ color: "#555", marginBottom: "15px" }}>{c.desc}</p>
              <button style={{
                padding: "10px 20px",
                backgroundColor: "#d6336c",
                color: "#fff",
                border: "none",
                borderRadius: "20px",
                cursor: "pointer",
                transition: "background-color 0.2s"
              }}
                onClick={() => navigate(c.route)}
              >
                Shop Now
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Footer */}
      <footer style={{ backgroundColor: "#000", color: "#fff", textAlign: "center", padding: "20px 0" }}>
        <div style={{ marginBottom: "10px" }}>
          <a href="/" style={{ color: "#fff", margin: "0 10px" }}>Home</a>
          <a href="/about" style={{ color: "#fff", margin: "0 10px" }}>About</a>
          <a href="/orders" style={{ color: "#fff", margin: "0 10px" }}>Orders</a>
          <a href="/wishlist" style={{ color: "#fff", margin: "0 10px" }}>Wishlist</a>
          <a href="/contact" style={{ color: "#fff", margin: "0 10px" }}>Contact</a>
        </div>
        <p>© 2025 Elegant Jewelry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default Collections;
